import React, { useRef, useEffect, useState } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Animated,
  Dimensions,
  Platform
} from 'react-native';
import { useNavigate } from 'react-router-dom';
import ServiceGrid from './ServiceGrid';

const HeroSection = ({ onCTAPress }) => {
  const [dimensions, setDimensions] = useState(() => {
    const { width, height } = Dimensions.get('window');
    return { width, height };
  });

  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(20)).current;
  const navigate = useNavigate();

  useEffect(() => {
    const updateDimensions = () => {
      const { width, height } = Dimensions.get('window');
      setDimensions({ width, height });
    };

    if (Platform.OS === 'web') {
      window.addEventListener('resize', updateDimensions);
    }

    const subscription = Dimensions.addEventListener('change', updateDimensions);

    return () => {
      if (Platform.OS === 'web') {
        window.removeEventListener('resize', updateDimensions);
      }
      subscription?.remove();
    };
  }, []);

  useEffect(() => {
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 800,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 800,
        useNativeDriver: true,
      }),
    ]).start();
  }, [fadeAnim, slideAnim]);

  const handleCTAPress = () => {
    navigate('/book');
  };

  const handleEnterpriseClick = () => {
    navigate('/book');
  };

  const services = [
    {
      icon: 'üéØ',
      title: 'Validation First',
      description: 'I validate ruthlessly before writing a single line of code'
    },
    {
      icon: 'ü§ù',
      title: 'Equity Partnership',
      description: 'When you succeed, I succeed - aligned incentives'
    },
    {
      icon: '‚ö°',
      title: 'AI-Native Speed',
      description: '2-12 weeks instead of 6+ months - because AI changes everything'
    },
    {
      icon: 'üíé',
      title: 'Premium & Selective',
      description: 'I only build ventures I\'d personally invest in'
    }
  ];

  const { width, height } = dimensions;
  const styles = getStyles(width, height);

  return (
    <View style={styles.container}>
      <View style={styles.backgroundGradient} />

      <Animated.View style={[
        styles.content, 
        { 
          opacity: fadeAnim,
          transform: [{ translateY: slideAnim }]
        }
      ]}>
        <Text style={styles.headline}>
          I Only Build What I Believe In
        </Text>

        <Text style={styles.subheadline}>
          Technical venture partner aligning my success with yours through equity partnership. Building MVPs in weeks, not months.
        </Text>

        <ServiceGrid services={services} variant="hero" />

        <View style={styles.trustIndicators}>
          <View style={styles.trustItem}>
            <Text style={styles.trustNumber}>2-12</Text>
            <Text style={styles.trustLabel}>Weeks to MVP</Text>
          </View>
          <View style={styles.trustDivider} />
          <View style={styles.trustItem}>
            <Text style={styles.trustNumber}>100%</Text>
            <Text style={styles.trustLabel}>Aligned Success</Text>
          </View>
          <View style={styles.trustDivider} />
          <View style={styles.trustItem}>
            <Text style={styles.trustNumber}>Equity</Text>
            <Text style={styles.trustLabel}>Partnership Model</Text>
          </View>
        </View>

        <TouchableOpacity
          style={styles.ctaButton}
          onPress={handleCTAPress}
          activeOpacity={0.8}
        >
          <Text style={styles.ctaButtonText}>Submit Your Venture</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.enterpriseLink}
          onPress={handleEnterpriseClick}
          activeOpacity={0.7}
        >
          <Text style={styles.enterpriseLinkText}>
            Only accepting founders building something extraordinary ‚Üí
          </Text>
        </TouchableOpacity>
      </Animated.View>
    </View>
  );
};

const getStyles = (width, height) => StyleSheet.create({
  container: {
    width: width,
    minHeight: height,
    justifyContent: 'center',
    alignItems: 'center',
    position: 'relative',
    paddingTop: 100,
    paddingBottom: 60,
  },
  backgroundGradient: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: '#0A0B0F',
    ...Platform.select({
      web: {
        background: 'linear-gradient(180deg, #0A0B0F 0%, #141520 100%)',
      },
    }),
  },
  content: {
    alignItems: 'center',
    paddingHorizontal: width > 768 ? 40 : 20,
    zIndex: 1,
    maxWidth: 1200,
    width: '100%',
  },
  headline: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 56 : width > 430 ? 40 : 32,
    fontWeight: '800',
    color: '#F3F4F6',
    textAlign: 'center',
    marginBottom: 24,
    letterSpacing: -1,
    lineHeight: width > 768 ? 64 : width > 430 ? 48 : 40,
  },
  subheadline: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 22 : 18,
    fontWeight: '400',
    color: '#9CA3AF',
    textAlign: 'center',
    marginBottom: 60,
    paddingHorizontal: width > 768 ? 0 : 20,
    lineHeight: width > 768 ? 32 : 28,
    maxWidth: 700,
  },
  trustIndicators: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 60,
    marginBottom: 40,
    paddingVertical: 20,
    paddingHorizontal: width > 768 ? 30 : 15,
    borderRadius: 12,
    backgroundColor: 'rgba(20, 21, 32, 0.7)',
    borderWidth: 1,
    borderColor: 'rgba(255, 255, 255, 0.06)',
    flexWrap: width <= 480 ? 'wrap' : 'nowrap',
    ...Platform.select({
      web: {
        boxShadow: '0 2px 12px rgba(0, 0, 0, 0.5)',
      },
    }),
  },
  trustItem: {
    alignItems: 'center',
    paddingHorizontal: width > 768 ? 30 : width > 480 ? 15 : 10,
    minWidth: width <= 480 ? 'calc(33.33% - 10px)' : 'auto',
    flexShrink: 1,
  },
  trustNumber: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 28 : width > 480 ? 24 : 20,
    fontWeight: '800',
    color: '#0EA5E9',
    marginBottom: 4,
    textAlign: 'center',
  },
  trustLabel: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 12 : width > 480 ? 11 : 10,
    color: '#9CA3AF',
    textTransform: 'uppercase',
    letterSpacing: width > 480 ? 1 : 0.5,
    textAlign: 'center',
    maxWidth: width <= 480 ? 80 : 'auto',
  },
  trustDivider: {
    width: 1,
    height: 40,
    backgroundColor: 'rgba(255, 255, 255, 0.06)',
    display: width <= 480 ? 'none' : 'flex',
  },
  ctaButton: {
    backgroundColor: '#0EA5E9',
    paddingVertical: 18,
    paddingHorizontal: 40,
    borderRadius: 8,
    marginBottom: 24,
    ...Platform.select({
      web: {
        transition: 'all 0.2s ease',
        cursor: 'pointer',
        ':hover': {
          backgroundColor: '#38BDF8',
          transform: 'translateY(-2px)',
          boxShadow: '0 10px 30px rgba(14, 165, 233, 0.4)',
        },
      },
    }),
  },
  ctaButtonText: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 18 : 16,
    color: '#FFFFFF',
    fontWeight: '700',
    letterSpacing: 0.5,
  },
  enterpriseLink: {
    paddingVertical: 10,
    paddingHorizontal: 20,
  },
  enterpriseLinkText: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 15 : 14,
    color: '#F59E0B',
    fontWeight: '500',
    textDecorationLine: 'underline',
    textDecorationStyle: 'solid',
    textDecorationColor: 'transparent',
    ...Platform.select({
      web: {
        transition: 'all 0.2s ease',
        ':hover': {
          textDecorationColor: '#F59E0B',
        },
      },
    }),
  },
});

export default HeroSection;
