import React, { useRef, useEffect } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Dimensions,
  Platform,
  Animated
} from 'react-native';
import { useNavigate } from 'react-router-dom';

const { width } = Dimensions.get('window');

const ServicesSection = () => {
  const navigate = useNavigate();
  const scaleAnim1 = useRef(new Animated.Value(1)).current;
  const scaleAnim2 = useRef(new Animated.Value(1)).current;
  const scaleAnim3 = useRef(new Animated.Value(1)).current;

  useEffect(() => {
    // Staggered scale animation for cards
    Animated.sequence([
      Animated.timing(scaleAnim1, {
        toValue: 1,
        duration: 600,
        useNativeDriver: true,
      }),
      Animated.timing(scaleAnim2, {
        toValue: 1,
        duration: 600,
        delay: 100,
        useNativeDriver: true,
      }),
      Animated.timing(scaleAnim3, {
        toValue: 1,
        duration: 600,
        delay: 200,
        useNativeDriver: true,
      }),
    ]).start();
  }, [scaleAnim1, scaleAnim2, scaleAnim3]);

  const handleCTAPress = () => {
    navigate('/book');
  };

  const services = [
    {
      title: 'Discovery Call',
      price: 'Free',
      duration: '30 minutes',
      description: "Let's see if we're a fit. I'll be honest about your idea's potential.",
      features: [
        'Candid assessment of your idea',
        'Market opportunity evaluation',
        'Technical feasibility check',
        'Go/No-go initial recommendation'
      ],
      cta: 'Book Reality Check',
      highlighted: false,
      gradient: 'linear-gradient(135deg, #667EEA 0%, #764BA2 100%)',
      animRef: scaleAnim1
    },
    {
      title: 'Validation Sprint',
      price: '$5,000',
      duration: '3 weeks',
      description: 'Deep dive into market validation before we build anything.',
      features: [
        'Market analysis & competitive research',
        'Customer problem validation',
        'Technical architecture planning',
        'Go/No-Go recommendation with pivot options',
        'Detailed validation report'
      ],
      cta: 'Start Validation',
      highlighted: true,
      gradient: 'linear-gradient(135deg, #0EA5E9 0%, #6366F1 100%)',
      animRef: scaleAnim2
    },
    {
      title: 'Build Partnership',
      price: 'Custom',
      duration: '2-12 weeks',
      description: 'Full MVP development with flexible deal structures based on your stage.',
      features: [
        'Idea Stage: 20-40% equity, lower cash',
        'Validated: 5-15% equity, market cash',
        'Funded: 2-5% equity, premium cash',
        '3 months post-launch support',
        'Ongoing partnership through exit'
      ],
      cta: 'Become a Partner',
      highlighted: false,
      gradient: 'linear-gradient(135deg, #F093FB 0%, #F5576C 100%)',
      animRef: scaleAnim3
    }
  ];

  return (
    <View style={styles.container}>
      {/* Background decoration */}
      <View style={styles.backgroundDecoration} />

      <View style={styles.headerSection}>
        <View style={styles.sectionBadge}>
          <Text style={styles.sectionBadgeText}>PARTNERSHIP OPTIONS</Text>
        </View>

        <Text style={styles.sectionTitle}>
          Choose Your Path to{' '}
          <Text style={styles.titleAccent}>Success</Text>
        </Text>

        <Text style={styles.sectionSubtitle}>
          Every founder starts somewhere. Let's find the right fit for your journey.
        </Text>
      </View>

      <View style={styles.servicesGrid}>
        {services.map((service, index) => (
          <Animated.View
            key={index}
            style={[
              styles.serviceCard,
              service.highlighted && styles.highlightedCard,
              { transform: [{ scale: service.animRef }] }
            ]}
          >
            {service.highlighted && (
              <View style={styles.popularRibbon}>
                <Text style={styles.popularText}>MOST POPULAR</Text>
              </View>
            )}

            <View style={[
              styles.cardGradient,
              { ...Platform.select({
                web: {
                  background: service.gradient,
                },
              })}
            ]} />

            <View style={styles.cardContent}>
              <Text style={styles.serviceTitle}>{service.title}</Text>

              <View style={styles.pricingRow}>
                <Text style={[
                  styles.price,
                  service.highlighted && styles.highlightedPrice
                ]}>
                  {service.price}
                </Text>
                <Text style={styles.duration}>{service.duration}</Text>
              </View>

              <Text style={styles.description}>{service.description}</Text>

              <View style={styles.featuresContainer}>
                {service.features.map((feature, idx) => (
                  <View key={idx} style={styles.featureItem}>
                    <View style={styles.featureBullet}>
                      <Text style={styles.featureBulletText}>✓</Text>
                    </View>
                    <Text style={styles.featureText}>{feature}</Text>
                  </View>
                ))}
              </View>

              <TouchableOpacity
                style={[
                  styles.ctaButton,
                  service.highlighted && styles.highlightedButton
                ]}
                onPress={handleCTAPress}
                activeOpacity={0.9}
              >
                <Text style={[
                  styles.ctaButtonText,
                  service.highlighted && styles.highlightedButtonText
                ]}>
