import React, { useRef, useEffect } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Animated,
  Dimensions,
  Platform
} from 'react-native';
import { useNavigate } from 'react-router-dom';

const { width, height } = Dimensions.get('window');

const HeroSection = ({ onCTAPress, onPathSelect, selectedPath }) => {
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(30)).current;
  const floatAnim = useRef(new Animated.Value(0)).current;
  const navigate = useNavigate();

  useEffect(() => {
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 1000,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 1000,
        useNativeDriver: true,
      }),
    ]).start();

    // Subtle floating animation
    Animated.loop(
      Animated.sequence([
        Animated.timing(floatAnim, {
          toValue: 1,
          duration: 3000,
          useNativeDriver: true,
        }),
        Animated.timing(floatAnim, {
          toValue: 0,
          duration: 3000,
          useNativeDriver: true,
        }),
      ])
    ).start();
  }, [fadeAnim, slideAnim, floatAnim]);

  const handleCTAPress = () => {
    navigate('/book');
  };

  const handlePathSelect = (path) => {
    if (onPathSelect) {
      onPathSelect(path);
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.gradientBackground}>
        {/* Subtle decorative elements */}
        <Animated.View 
          style={[
            styles.floatingElement,
            styles.floatingElement1,
            {
              transform: [{
                translateY: floatAnim.interpolate({
                  inputRange: [0, 1],
                  outputRange: [0, -20]
                })
              }]
            }
          ]}
        />
        <Animated.View 
          style={[
            styles.floatingElement,
            styles.floatingElement2,
            {
              transform: [{
                translateY: floatAnim.interpolate({
                  inputRange: [0, 1],
                  outputRange: [0, 20]
                })
              }]
            }
          ]}
        />
      </View>

      <Animated.View style={[
        styles.content, 
        { 
          opacity: fadeAnim,
          transform: [{ translateY: slideAnim }]
        }
      ]}>
        <Text style={styles.headline}>
          AI Integration Expert & Technical Founder
        </Text>

        <View style={styles.subtitleContainer}>
          <Text style={styles.subtitle}>
            Helping enterprises adopt AI efficiently. Building MVPs that get funded.
          </Text>
        </View>

        <Text style={styles.choicePrompt}>
          Select what best describes you:
        </Text>

        <View style={styles.pathButtonsContainer}>
          <TouchableOpacity
            style={[
              styles.pathButton, 
              styles.enterpriseButton,
              selectedPath === 'enterprise' && styles.selectedButton
            ]}
            onPress={() => handlePathSelect('enterprise')}
            activeOpacity={0.8}
          >
            <Text style={styles.pathIcon}>üè¢</Text>
            <Text style={[styles.pathButtonTitle, selectedPath === 'enterprise' && styles.selectedButtonText]}>I run an established business</Text>
            <Text style={[styles.pathButtonSubtext, selectedPath === 'enterprise' && styles.selectedButtonSubtext]}>Looking to integrate AI efficiently</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={[
              styles.pathButton, 
              styles.startupButton,
              selectedPath === 'startup' && styles.selectedButton
            ]}
            onPress={() => handlePathSelect('startup')}
            activeOpacity={0.8}
          >
            <Text style={styles.pathIcon}>üöÄ</Text>
            <Text style={[styles.pathButtonTitle, selectedPath === 'startup' && styles.selectedButtonText]}>I have a startup idea</Text>
            <Text style={[styles.pathButtonSubtext, selectedPath === 'startup' && styles.selectedButtonSubtext]}>Need an MVP or technical co-founder</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.scrollIndicator}>
          <View style={styles.scrollDot} />
          <Text style={styles.scrollText}>SCROLL TO EXPLORE</Text>
        </View>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    width: width,
    minHeight: height,
    justifyContent: 'center',
    alignItems: 'center',
    position: 'relative',
    paddingTop: 80,
  },
  gradientBackground: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: '#0A0A0A',
    ...Platform.select({
      web: {
        background: 'linear-gradient(135deg, #0A0A0A 0%, #1A1A1A 100%)',
      },
    }),
  },
  content: {
    alignItems: 'center',
    paddingHorizontal: 20,
    zIndex: 1,
  },
  headline: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 48 : 28,
    fontWeight: '800',
    color: '#FFFFFF',
    textAlign: 'center',
    marginBottom: 20,
    letterSpacing: width > 768 ? 1 : 0,
    lineHeight: width > 768 ? 56 : 36,
  },
  subtitleContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
  },
  subtitle: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 20 : 16,
    fontWeight: '400',
    color: '#CCCCCC',
    textAlign: 'center',
    marginHorizontal: 10,
    paddingHorizontal: width > 768 ? 0 : 20,
    lineHeight: width > 768 ? 28 : 24,
    marginBottom: 30,
  },
  choicePrompt: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 16 : 14,
    color: '#FFA500',
    textAlign: 'center',
    marginBottom: 20,
    fontWeight: '600',
    letterSpacing: 0.5,
  },
  pathButtonsContainer: {
    flexDirection: width > 768 ? 'row' : 'column',
    gap: 20,
    alignItems: 'center',
    marginBottom: 60,
  },
  pathButton: {
    backgroundColor: 'rgba(255, 255, 255, 0.05)',
    borderWidth: 1,
    borderRadius: 8,
    padding: width > 768 ? 30 : 25,
    width: width > 768 ? 320 : width * 0.9,
    maxWidth: 380,
    alignItems: 'center',
    position: 'relative',
    overflow: 'hidden',
    ...Platform.select({
      web: {
        transition: 'all 0.3s ease',
        ':hover': {
          backgroundColor: 'rgba(255, 255, 255, 0.1)',
          transform: 'translateY(-2px)',
        },
      },
    }),
  },
  enterpriseButton: {
    borderColor: 'rgba(74, 144, 226, 0.5)',
  },
  startupButton: {
    borderColor: 'rgba(255, 69, 0, 0.5)',
  },
  pathIcon: {
    fontSize: 36,
    marginBottom: 15,
  },
  pathButtonTitle: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 18 : 16,
    color: '#FFFFFF',
    marginBottom: 8,
    textAlign: 'center',
    fontWeight: '700',
  },
  pathButtonSubtext: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 14 : 12,
    color: '#999999',
    textAlign: 'center',
  },
  selectedButton: {
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    borderWidth: 2,
    borderColor: '#FF4500',
    transform: [{ scale: 1.02 }],
  },
  selectedButtonText: {
    color: '#FF4500',
    fontWeight: '800',
  },
  selectedButtonSubtext: {
    color: '#FFA500',
  },
  ctaButton: {
    marginBottom: 60,
  },
  ctaButtonInner: {
    backgroundColor: '#FF4500',
    paddingVertical: 20,
    paddingHorizontal: 40,
    borderRadius: 8,
    borderWidth: 2,
    borderColor: '#FFA500',
    position: 'relative',
    overflow: 'hidden',
  },
  ctaButtonText: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 16 : 12,
    color: '#000000',
    fontWeight: '900',
    letterSpacing: 1,
  },
  ctaButtonGlow: {
    position: 'absolute',
    top: -50,
    left: -50,
    right: -50,
    bottom: -50,
    backgroundColor: 'transparent',
    ...Platform.select({
      web: {
        boxShadow: '0 0 40px rgba(255, 69, 0, 0.8)',
      },
      default: {
        shadowColor: '#FF4500',
        shadowOffset: { width: 0, height: 0 },
        shadowOpacity: 0.8,
        shadowRadius: 40,
      },
    }),
  },
  scrollIndicator: {
    alignItems: 'center',
    position: 'absolute',
    bottom: 30,
  },
  scrollDot: {
    width: 8,
    height: 8,
    backgroundColor: '#FF4500',
    borderRadius: 4,
    marginBottom: 10,
  },
  scrollText: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: 12,
    color: '#FFFFFF',
    opacity: 0.6,
    letterSpacing: 2,
  },
  floatingElement: {
    position: 'absolute',
    borderRadius: '50%',
    opacity: 0.1,
  },
  floatingElement1: {
    width: 300,
    height: 300,
    backgroundColor: '#4A90E2',
    top: '10%',
    left: '-10%',
    ...Platform.select({
      web: {
        filter: 'blur(40px)',
      },
    }),
  },
  floatingElement2: {
    width: 400,
    height: 400,
    backgroundColor: '#FF4500',
    bottom: '10%',
    right: '-15%',
    ...Platform.select({
      web: {
        filter: 'blur(60px)',
      },
    }),
  },
});

export default HeroSection;
