import React, { useRef, useEffect } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Animated,
  Dimensions,
  Platform,
  Linking
} from 'react-native';

const { width, height } = Dimensions.get('window');

const HeroSection = ({ onCTAPress, onPathSelect }) => {
  const fireAnim = useRef(new Animated.Value(0)).current;
  const scaleAnim = useRef(new Animated.Value(1)).current;
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const particleAnims = useRef([...Array(20)].map(() => new Animated.Value(0))).current;

  useEffect(() => {
    Animated.timing(fadeAnim, {
      toValue: 1,
      duration: 1500,
      useNativeDriver: true,
    }).start();

    particleAnims.forEach((anim, index) => {
      Animated.loop(
        Animated.sequence([
          Animated.delay(index * 100),
          Animated.timing(anim, {
            toValue: 1,
            duration: 3000,
            useNativeDriver: true,
          }),
          Animated.timing(anim, {
            toValue: 0,
            duration: 0,
            useNativeDriver: true,
          }),
        ])
      ).start();
    });
  }, [fadeAnim, fireAnim, scaleAnim, particleAnims]);

  const handleCTAPress = () => {
    Linking.openURL('https://calendly.com/zappstudios');
  };

  const handlePathSelect = (path) => {
    if (onPathSelect) {
      onPathSelect(path);
    }
  };

  const renderFireParticles = () => {
    return particleAnims.map((anim, index) => {
      const randomX = Math.random() * width;
      const translateY = anim.interpolate({
        inputRange: [0, 1],
        outputRange: [0, -height],
      });
      const opacity = anim.interpolate({
        inputRange: [0, 0.2, 0.8, 1],
        outputRange: [0, 1, 1, 0],
      });

      return (
        <Animated.View
          key={index}
          style={[
            styles.particle,
            {
              left: randomX,
              transform: [{ translateY }],
              opacity,
            },
          ]}
        />
      );
    });
  };

  return (
    <View style={styles.container}>
      <View style={styles.gradientBackground}>
        {renderFireParticles()}
      </View>

      <Animated.View style={[styles.content, { opacity: fadeAnim }]}>
        <Text style={styles.headline}>
          AI Integration Expert & Technical Founder
        </Text>

        <View style={styles.subtitleContainer}>
          <Text style={styles.subtitle}>
            Helping enterprises adopt AI efficiently. Building MVPs that get funded.
          </Text>
        </View>

        <View style={styles.pathButtonsContainer}>
          <TouchableOpacity
            style={[styles.pathButton, styles.enterpriseButton]}
            onPress={() => handlePathSelect('enterprise')}
            activeOpacity={0.8}
          >
            <Text style={styles.pathIcon}>üè¢</Text>
            <Text style={styles.pathButtonTitle}>AI Consulting</Text>
            <Text style={styles.pathButtonSubtext}>For Enterprises</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={[styles.pathButton, styles.startupButton]}
            onPress={() => handlePathSelect('startup')}
            activeOpacity={0.8}
          >
            <Text style={styles.pathIcon}>üöÄ</Text>
            <Text style={styles.pathButtonTitle}>Product Development</Text>
            <Text style={styles.pathButtonSubtext}>For Startups</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.scrollIndicator}>
          <Animated.View style={[styles.scrollDot, { transform: [{ translateY: fireAnim.interpolate({
            inputRange: [0, 1],
            outputRange: [0, 10]
          }) }] }]} />
          <Text style={styles.scrollText}>SCROLL TO EXPLORE</Text>
        </View>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    width: width,
    minHeight: height,
    justifyContent: 'center',
    alignItems: 'center',
    position: 'relative',
    paddingTop: 80,
  },
  gradientBackground: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: '#000000',
    ...Platform.select({
      web: {
        background: 'linear-gradient(180deg, #000000 0%, #8B0000 50%, #FF4500 100%)',
      },
    }),
  },
  particle: {
    position: 'absolute',
    width: 4,
    height: 4,
    backgroundColor: '#FFA500',
    borderRadius: 2,
    ...Platform.select({
      web: {
        boxShadow: '0 0 10px rgba(255, 165, 0, 0.8)',
      },
      default: {
        shadowColor: '#FFA500',
        shadowOffset: { width: 0, height: 0 },
        shadowOpacity: 0.8,
        shadowRadius: 10,
      },
    }),
  },
  content: {
    alignItems: 'center',
    paddingHorizontal: 20,
    zIndex: 1,
  },
  headline: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 64 : 28,
    fontWeight: '900',
    color: '#FF4500',
    textAlign: 'center',
    marginBottom: 20,
    letterSpacing: width > 768 ? 2 : 0,
  },
  subtitleContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
  },
  subtitle: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 20 : 14,
    fontWeight: '700',
    color: '#FFA500',
    textAlign: 'center',
    marginHorizontal: 10,
    paddingHorizontal: width > 768 ? 0 : 20,
    lineHeight: width > 768 ? 28 : 20,
  },
  subtitleFire: {
    fontSize: 24,
  },
  tagline: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 18 : 14,
    color: '#FFFFFF',
    textAlign: 'center',
    marginBottom: 30,
    opacity: 0.9,
  },
  pathButtonsContainer: {
    flexDirection: width > 768 ? 'row' : 'column',
    gap: 20,
    alignItems: 'center',
    marginBottom: 60,
  },
  pathButton: {
    backgroundColor: 'rgba(255, 69, 0, 0.1)',
    borderWidth: 2,
    borderRadius: 12,
    padding: width > 768 ? 25 : 20,
    width: width > 768 ? 280 : width * 0.85,
    maxWidth: 350,
    alignItems: 'center',
    position: 'relative',
    overflow: 'hidden',
  },
  enterpriseButton: {
    borderColor: '#4A90E2',
  },
  startupButton: {
    borderColor: '#FF4500',
  },
  pathIcon: {
    fontSize: 36,
    marginBottom: 15,
  },
  pathButtonTitle: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 14 : 12,
    color: '#FFFFFF',
    marginBottom: 8,
    textAlign: 'center',
  },
  pathButtonSubtext: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 12 : 10,
    color: '#FFA500',
    textAlign: 'center',
  },
  ctaButton: {
    marginBottom: 60,
  },
  ctaButtonInner: {
    backgroundColor: '#FF4500',
    paddingVertical: 20,
    paddingHorizontal: 40,
    borderRadius: 8,
    borderWidth: 2,
    borderColor: '#FFA500',
    position: 'relative',
    overflow: 'hidden',
  },
  ctaButtonText: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 16 : 12,
    color: '#000000',
    fontWeight: '900',
    letterSpacing: 1,
  },
  ctaButtonGlow: {
    position: 'absolute',
    top: -50,
    left: -50,
    right: -50,
    bottom: -50,
    backgroundColor: 'transparent',
    ...Platform.select({
      web: {
        boxShadow: '0 0 40px rgba(255, 69, 0, 0.8)',
      },
      default: {
        shadowColor: '#FF4500',
        shadowOffset: { width: 0, height: 0 },
        shadowOpacity: 0.8,
        shadowRadius: 40,
      },
    }),
  },
  scrollIndicator: {
    alignItems: 'center',
    position: 'absolute',
    bottom: 30,
  },
  scrollDot: {
    width: 8,
    height: 8,
    backgroundColor: '#FF4500',
    borderRadius: 4,
    marginBottom: 10,
  },
  scrollText: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: 12,
    color: '#FFFFFF',
    opacity: 0.6,
    letterSpacing: 2,
  },
});

export default HeroSection;
