import React, { useRef, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Animated,
  Dimensions,
  Platform
} from 'react-native';

const { width } = Dimensions.get('window');

const ProcessStep = ({ step, index }) => {
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(30)).current;

  useEffect(() => {
    Animated.sequence([
      Animated.delay(index * 200),
      Animated.parallel([
        Animated.timing(fadeAnim, {
          toValue: 1,
          duration: 600,
          useNativeDriver: true,
        }),
        Animated.timing(slideAnim, {
          toValue: 0,
          duration: 600,
          useNativeDriver: true,
        }),
      ]),
    ]).start();
  }, [fadeAnim, slideAnim, index]);

  return (
    <Animated.View
      style={[
        styles.stepContainer,
        {
          opacity: fadeAnim,
          transform: [{ translateY: slideAnim }],
        },
      ]}
    >
      <View style={styles.stepNumberContainer}>
        <Text style={styles.stepNumber}>{step.number}</Text>
        <View style={styles.stepNumberGlow} />
      </View>

      <View style={styles.stepContent}>
        <View style={styles.stepHeader}>
          <Text style={styles.stepIcon}>{step.icon}</Text>
          <Text style={styles.stepTitle}>{step.title}</Text>
        </View>
        <Text style={styles.stepDescription}>{step.description}</Text>
      </View>

      {index < 3 && (
        <View style={styles.connector}>
          <View style={styles.connectorLine} />
          <Text style={styles.connectorArrow}>‚Üì</Text>
        </View>
      )}
    </Animated.View>
  );
};

const HowItWorksSection = () => {
  const pulseAnim = useRef(new Animated.Value(1)).current;

  useEffect(() => {
    const pulseAnimation = Animated.loop(
      Animated.sequence([
        Animated.timing(pulseAnim, {
          toValue: 1.1,
          duration: 1500,
          useNativeDriver: true,
        }),
        Animated.timing(pulseAnim, {
          toValue: 1,
          duration: 1500,
          useNativeDriver: true,
        }),
      ])
    );
    pulseAnimation.start();
    return () => pulseAnimation.stop();
  }, [pulseAnim]);

  const steps = [
    {
      number: '01',
      icon: 'üîç',
      title: 'Spot Market Gaps',
      description: 'We identify inefficiencies in your business operations and untapped market opportunities that can be solved with custom software.',
    },
    {
      number: '02',
      icon: '‚öíÔ∏è',
      title: 'Forge Custom Software',
      description: 'Using cutting-edge AI, mobile, and web technologies, we build tailored solutions that address your specific business needs.',
    },
    {
      number: '03',
      icon: 'üî•',
      title: 'Ignite Efficiency & Cash Flow',
      description: 'Deploy powerful tools that streamline operations, reduce costs, and create new revenue streams for sustainable growth.',
    },
    {
      number: '04',
      icon: 'üöÄ',
      title: 'Scale for Success',
      description: 'Continuously optimize and expand your software capabilities as your business grows, ensuring long-term competitive advantage.',
    },
  ];

  return (
    <View style={styles.container}>
      <Text style={styles.sectionTitle}>THE FORGE PROCESS</Text>
      <View style={styles.fireBar} />

      <Text style={styles.sectionSubtitle}>
        From concept to cash flow in four strategic steps
      </Text>

      <View style={styles.stepsContainer}>
        {steps.map((step, index) => (
          <ProcessStep key={index} step={step} index={index} />
        ))}
      </View>

      <Animated.View
        style={[
          styles.ctaContainer,
          {
            transform: [{ scale: pulseAnim }],
          },
        ]}
      >
        <Text style={styles.ctaText}>Ready to ignite your business?</Text>
        <Text style={styles.ctaSubtext}>Let's forge your custom solution</Text>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#000000',
    paddingVertical: 80,
    paddingHorizontal: width > 768 ? 40 : 20,
    borderTopWidth: 2,
    borderTopColor: '#8B0000',
  },
  sectionTitle: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 36 : 24,
    color: '#FF4500',
    textAlign: 'center',
    marginBottom: 20,
    textShadowColor: '#FF4500',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 20,
  },
  fireBar: {
    height: 4,
    width: 100,
    backgroundColor: '#FF4500',
    alignSelf: 'center',
    marginBottom: 20,
    borderRadius: 2,
    ...Platform.select({
      web: {
        boxShadow: '0 2px 20px rgba(255, 69, 0, 0.8)',
      },
      default: {
        shadowColor: '#FF4500',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.8,
        shadowRadius: 20,
      },
    }),
  },
  sectionSubtitle: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 18 : 14,
    color: '#CCCCCC',
    textAlign: 'center',
    marginBottom: 60,
  },
  stepsContainer: {
    maxWidth: 800,
    alignSelf: 'center',
    width: '100%',
  },
  stepContainer: {
    flexDirection: 'row',
    alignItems: 'flex-start',
    marginBottom: 40,
    position: 'relative',
  },
  stepNumberContainer: {
    position: 'relative',
    marginRight: 30,
  },
  stepNumber: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 28 : 20,
    color: '#FF4500',
    width: 60,
    height: 60,
    textAlign: 'center',
    lineHeight: 60,
    backgroundColor: 'rgba(255, 69, 0, 0.1)',
    borderRadius: 30,
    borderWidth: 2,
    borderColor: '#FF4500',
  },
  stepNumberGlow: {
    position: 'absolute',
    top: -10,
    left: -10,
    right: -10,
    bottom: -10,
    borderRadius: 40,
    backgroundColor: 'transparent',
    ...Platform.select({
      web: {
        boxShadow: '0 0 30px rgba(255, 69, 0, 0.4)',
      },
      default: {
        shadowColor: '#FF4500',
        shadowOffset: { width: 0, height: 0 },
        shadowOpacity: 0.4,
        shadowRadius: 30,
      },
    }),
  },
  stepContent: {
    flex: 1,
    paddingTop: 5,
  },
  stepHeader: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  stepIcon: {
    fontSize: 28,
    marginRight: 15,
  },
  stepTitle: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 20 : 16,
    color: '#FFA500',
    fontWeight: '700',
  },
  stepDescription: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 14 : 12,
    color: '#CCCCCC',
    lineHeight: 22,
    paddingLeft: 43,
  },
  connector: {
    position: 'absolute',
    left: 28,
    top: 70,
    alignItems: 'center',
  },
  connectorLine: {
    width: 2,
    height: 40,
    backgroundColor: '#FF4500',
    opacity: 0.3,
  },
  connectorArrow: {
    fontSize: 16,
    color: '#FF4500',
    marginTop: -5,
    opacity: 0.5,
  },
  ctaContainer: {
    marginTop: 60,
    alignItems: 'center',
    padding: 30,
    backgroundColor: 'rgba(255, 69, 0, 0.05)',
    borderRadius: 10,
    borderWidth: 1,
    borderColor: '#8B0000',
    maxWidth: 600,
    alignSelf: 'center',
  },
  ctaText: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 20 : 16,
    color: '#FF4500',
    marginBottom: 10,
    textAlign: 'center',
  },
  ctaSubtext: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 16 : 14,
    color: '#FFA500',
    textAlign: 'center',
  },
});

export default HowItWorksSection;
