import React, { useEffect, useRef, useState } from 'react';
import {
  View,
  ScrollView,
  StyleSheet,
  Animated,
  Platform,
  Linking
} from 'react-native';
import { useNavigate } from 'react-router-dom';
import NavigationBar from '../components/NavigationBar';
import HeroSection from '../components/HeroSection';
import AboutSection from '../components/AboutSection';
import ProjectsSection from '../components/ProjectsSection';
import HowItWorksSection from '../components/HowItWorksSection';
import ContactSection from '../components/ContactSection';
import Footer from '../components/Footer';
import EnterpriseSection from '../components/EnterpriseSection';
import StartupSection from '../components/StartupSection';

const HomePage = () => {
  const scrollViewRef = useRef(null);
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const [activeSection, setActiveSection] = useState('home');
  const [selectedPath, setSelectedPath] = useState(null);
  const navigate = useNavigate();

  const sectionRefs = {
    home: useRef(null),
    enterprise: useRef(null),
    startup: useRef(null),
    about: useRef(null),
    projects: useRef(null),
    how: useRef(null),
    contact: useRef(null),
  };

  useEffect(() => {
    Animated.timing(fadeAnim, {
      toValue: 1,
      duration: 1000,
      useNativeDriver: true,
    }).start();
  }, [fadeAnim]);

  const handlePathSelect = (path) => {
    setSelectedPath(path);
  };

  // Handle scrolling after the path is selected and sections are rendered
  useEffect(() => {
    if (!selectedPath) return;

    // Small delay to ensure DOM is updated
    const scrollTimer = setTimeout(() => {
      const sectionName = selectedPath === 'enterprise' ? 'enterprise' : 'startup';
      const sectionRef = sectionRefs[sectionName];

      if (Platform.OS === 'web') {
        // For web, use native smooth scrolling for better performance on iOS
        const element = document.getElementById(sectionName + '-section');
        if (element) {
          // Use native smooth scroll behavior for iOS Safari compatibility
          element.scrollIntoView({ 
            behavior: 'smooth', 
            block: 'start',
            inline: 'nearest' 
          });
        }
      } else if (sectionRef && sectionRef.current && scrollViewRef.current) {
        // For native, use the existing measureLayout method
        sectionRef.current.measureLayout(
          scrollViewRef.current,
          (x, y) => {
            const startY = 0;
            const endY = y - 80;
            const duration = 1500; // 1.5 seconds for gradual scroll

            const startTime = Date.now();
            const animateScroll = () => {
              const currentTime = Date.now();
              const elapsed = currentTime - startTime;
              const progress = Math.min(elapsed / duration, 1);

              // Easing function for very smooth, gradual animation
              const easeInOutCubic = progress < 0.5
                ? 4 * progress * progress * progress
                : 1 - Math.pow(-2 * progress + 2, 3) / 2;

              const currentY = startY + (endY - startY) * easeInOutCubic;
              scrollViewRef.current.scrollTo({ y: currentY, animated: false });

              if (progress < 1) {
                requestAnimationFrame(animateScroll);
              }
            };
            animateScroll();
          },
          () => {}
        );
      }
    }, 100); // Small delay to ensure DOM update

    return () => clearTimeout(scrollTimer);
  }, [selectedPath]);

  const scrollToSection = (sectionName) => {
    if (sectionName === 'careers') {
      if (Platform.OS === 'web') {
        navigate('/careers');
      } else {
        Linking.openURL('/careers');
      }
      return;
    }

    setActiveSection(sectionName);
    const sectionRef = sectionRefs[sectionName];
    if (sectionRef && sectionRef.current && scrollViewRef.current) {
      sectionRef.current.measureLayout(
        scrollViewRef.current,
        (x, y) => {
          scrollViewRef.current.scrollTo({ y: y - 80, animated: true });
        },
        () => {}
      );
    }
  };

  const handleScroll = (event) => {
    const scrollY = event.nativeEvent.contentOffset.y;

    Object.entries(sectionRefs).forEach(([name, ref]) => {
      if (ref.current) {
        ref.current.measureLayout(
          scrollViewRef.current,
          (x, y, width, height) => {
            if (scrollY >= y - 100 && scrollY < y + height - 100) {
              setActiveSection(name);
            }
          },
          () => {}
        );
      }
    });
  };

  return (
    <Animated.View style={[styles.container, { opacity: fadeAnim }]}>
      <NavigationBar 
        activeSection={activeSection} 
        onNavigate={scrollToSection} 
      />

      <ScrollView
        ref={scrollViewRef}
        style={styles.scrollView}
        contentContainerStyle={styles.scrollContent}
        showsVerticalScrollIndicator={false}
        onScroll={handleScroll}
        scrollEventThrottle={16}
      >
        <View ref={sectionRefs.home}>
          <HeroSection 
            onCTAPress={() => scrollToSection('contact')} 
            onPathSelect={handlePathSelect}
          />
        </View>

        {selectedPath === 'enterprise' && (
          <>
            <View ref={sectionRefs.enterprise} id="enterprise-section">
              <EnterpriseSection />
            </View>
            <View ref={sectionRefs.about}>
              <AboutSection selectedPath={selectedPath} />
            </View>
          </>
        )}

        {selectedPath === 'startup' && (
          <>
            <View ref={sectionRefs.startup} id="startup-section">
              <StartupSection />
            </View>
            <View ref={sectionRefs.projects}>
              <ProjectsSection />
            </View>
            <View ref={sectionRefs.about}>
              <AboutSection selectedPath={selectedPath} />
            </View>
          </>
        )}

        <View ref={sectionRefs.contact}>
          <ContactSection />
        </View>

        <Footer />
      </ScrollView>
    </Animated.View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000000',
  },
  scrollView: {
    flex: 1,
  },
  scrollContent: {
    flexGrow: 1,
  },
});

export default HomePage;
