import React from 'react';
import {
  View,
  Text,
  StyleSheet,
  Dimensions,
  Platform
} from 'react-native';

const { width } = Dimensions.get('window');

const ServiceGrid = ({ services, variant = 'primary' }) => {
  const styles = getStyles(width, variant);

  return (
    <View style={styles.container}>
      <View style={styles.grid}>
        {services.map((service, index) => (
          <View key={index} style={styles.serviceCard}>
            <Text style={styles.serviceIcon}>{service.icon}</Text>
            <Text style={styles.serviceTitle}>{service.title}</Text>
            <Text style={styles.serviceDescription}>{service.description}</Text>
          </View>
        ))}
      </View>
    </View>
  );
};

const getStyles = (screenWidth, variant) => StyleSheet.create({
  container: {
    width: '100%',
    maxWidth: 1200,
    alignSelf: 'center',
  },
  grid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
    gap: 20,
  },
  serviceCard: {
    backgroundColor: variant === 'primary' ? '#16161A' : 'transparent',
    borderRadius: 12,
    padding: screenWidth > 768 ? 30 : 24,
    width: screenWidth > 1024 ? 'calc(25% - 15px)' : screenWidth > 768 ? 'calc(50% - 10px)' : '100%',
    borderWidth: 1,
    borderColor: 'rgba(255, 255, 255, 0.08)',
    minHeight: 200,
    ...Platform.select({
      web: {
        transition: 'all 0.2s ease',
        cursor: 'default',
        ':hover': {
          borderColor: variant === 'primary' ? '#5E81F4' : '#52E5A5',
          transform: 'translateY(-4px)',
          backgroundColor: variant === 'primary' ? '#1A1A1F' : 'rgba(82, 229, 165, 0.05)',
        },
      },
    }),
  },
  serviceIcon: {
    fontSize: 36,
    marginBottom: 16,
  },
  serviceTitle: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: screenWidth > 768 ? 18 : 16,
    fontWeight: '700',
    color: '#FFFFFF',
    marginBottom: 12,
    lineHeight: 24,
  },
  serviceDescription: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: screenWidth > 768 ? 14 : 13,
    color: '#A0A0A8',
    lineHeight: screenWidth > 768 ? 22 : 20,
  },
});

export default ServiceGrid;
