import React from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Platform,
  Dimensions
} from 'react-native';

const { width } = Dimensions.get('window');

const NavigationBar = ({ activeSection, onNavigate }) => {

  const handleBookNow = () => {
    if (Platform.OS === 'web') {
      window.open('https://calendly.com/zappstudios', '_blank');
    } else {
      Linking.openURL('https://calendly.com/zappstudios');
    }
  };

  const handleLogoClick = () => {
    if (Platform.OS === 'web') {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    } else {
      onNavigate('home');
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.navbar}>
        <TouchableOpacity 
          style={styles.logoContainer}
          onPress={handleLogoClick}
          activeOpacity={0.7}
        >
          <Text style={styles.logo}>ZAPP</Text>
          <Text style={styles.logoAccent}>STUDIOS</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.bookButton}
          onPress={handleBookNow}
          activeOpacity={0.8}
        >
          <Text style={styles.bookButtonText}>BOOK NOW</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    position: Platform.OS === 'web' ? 'fixed' : 'absolute',
    top: 0,
    left: 0,
    right: 0,
    zIndex: 1000,
    backgroundColor: 'rgba(0, 0, 0, 0.95)',
    ...Platform.select({
      web: {
        backdropFilter: 'blur(10px)',
      },
    }),
  },
  navbar: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    paddingHorizontal: width > 768 ? 40 : 20,
    paddingVertical: 20,
    borderBottomWidth: 2,
    borderBottomColor: '#FF4500',
    ...Platform.select({
      web: {
        boxShadow: '0 4px 20px rgba(255, 69, 0, 0.3)',
      },
      default: {
        shadowColor: '#FF4500',
        shadowOffset: { width: 0, height: 4 },
        shadowOpacity: 0.3,
        shadowRadius: 20,
        elevation: 10,
      },
    }),
  },
  logoContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  logo: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 24 : 18,
    color: '#FF4500',
    fontWeight: '900',
  },
  logoAccent: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 24 : 18,
    color: '#FFFFFF',
    fontWeight: '900',
    marginLeft: 8,
  },
  navItems: {
    flexDirection: 'row',
    alignItems: 'center',
    gap: width > 768 ? 30 : 15,
  },
  navItem: {
    paddingVertical: 8,
    paddingHorizontal: width > 768 ? 16 : 8,
    position: 'relative',
  },
  navItemActive: {
    transform: [{ scale: 1.1 }],
  },
  navText: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 14 : 12,
    fontWeight: '700',
    color: '#FFFFFF',
    opacity: 0.8,
    letterSpacing: 1,
  },
  navTextActive: {
    color: '#FF4500',
    opacity: 1,
    textShadowColor: '#FF4500',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 5,
  },
  activeIndicator: {
    position: 'absolute',
    bottom: -2,
    left: 0,
    right: 0,
    height: 3,
    backgroundColor: '#FF4500',
    borderRadius: 2,
    ...Platform.select({
      web: {
        boxShadow: '0 2px 10px rgba(255, 69, 0, 0.8)',
      },
      default: {
        shadowColor: '#FF4500',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.8,
        shadowRadius: 10,
      },
    }),
  },
});

export default NavigationBar;
