import React from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  StyleSheet,
  Dimensions,
  Platform,
  Linking
} from 'react-native';
import { useNavigate } from 'react-router-dom';
import Footer from '../components/Footer';

const { width } = Dimensions.get('window');

const JobCard = ({ job }) => {
  const handleApply = () => {
    Linking.openURL(job.link);
  };

  return (
    <TouchableOpacity
      style={styles.jobCard}
      onPress={handleApply}
      activeOpacity={0.9}
    >
      <View style={styles.cardHeader}>
        <View style={styles.jobTypeContainer}>
          <Text style={styles.jobType}>{job.type}</Text>
        </View>
        <Text style={styles.jobLocation}>{job.location}</Text>
      </View>

      <Text style={styles.jobTitle}>{job.title}</Text>
      <Text style={styles.jobDescription}>{job.description}</Text>

      <View style={styles.requirementsContainer}>
        <Text style={styles.requirementsTitle}>Key Requirements:</Text>
        {job.requirements.map((req, idx) => (
          <View key={idx} style={styles.requirementItem}>
            <Text style={styles.requirementBullet}>‚ñ∏</Text>
            <Text style={styles.requirementText}>{req}</Text>
          </View>
        ))}
      </View>

      <View style={styles.tagsContainer}>
        {job.tags.map((tag, idx) => (
          <View key={idx} style={styles.tagBadge}>
            <Text style={styles.tagText}>{tag}</Text>
          </View>
        ))}
      </View>

      <TouchableOpacity
        style={styles.applyButton}
        onPress={handleApply}
      >
        <Text style={styles.applyButtonText}>APPLY NOW</Text>
        <Text style={styles.applyArrow}>‚Üí</Text>
      </TouchableOpacity>
    </TouchableOpacity>
  );
};

const Careers = () => {
  const navigate = useNavigate();
  const jobs = [
    {
      id: 1,
      title: 'Full-Stack Developer',
      type: 'FULL-TIME',
      location: 'REMOTE',
      description: 'Build scalable applications that drive business growth. Work with modern technologies to create impactful solutions.',
      requirements: [
        'React/React Native expertise',
        'Node.js/Python backend skills',
        'Database design experience',
      ],
      tags: ['React', 'Node.js', 'PostgreSQL', 'AWS'],
      link: 'https://wellfound.com/jobs/fullstack-dev',
    },
    {
      id: 2,
      title: 'AI/ML Engineer',
      type: 'FULL-TIME',
      location: 'HYBRID',
      description: 'Develop intelligent solutions using cutting-edge AI. Transform business operations with machine learning.',
      requirements: [
        'TensorFlow/PyTorch expertise',
        'Production ML deployment',
        'Data pipeline architecture',
      ],
      tags: ['Python', 'TensorFlow', 'MLOps', 'Cloud'],
      link: 'https://wellfound.com/jobs/ai-engineer',
    },
    {
      id: 3,
      title: 'Mobile App Developer',
      type: 'CONTRACT',
      location: 'REMOTE',
      description: 'Create exceptional mobile experiences. Build apps that users love and businesses depend on.',
      requirements: [
        'React Native proficiency',
        'iOS/Android native knowledge',
        'API integration skills',
      ],
      tags: ['React Native', 'Firebase', 'Mobile', 'UX'],
      link: 'https://wellfound.com/jobs/mobile-dev',
    },
  ];

  const handleBackToHome = () => {
    navigate('/');
  };

  return (
    <View style={styles.container}>
      <View style={styles.navHeader}>
        <TouchableOpacity onPress={handleBackToHome} style={styles.backButton}>
          <Text style={styles.backArrow}>‚Üê</Text>
          <Text style={styles.backText}>Back to Home</Text>
        </TouchableOpacity>
      </View>

      <ScrollView
        style={styles.scrollView}
        contentContainerStyle={styles.scrollContent}
        showsVerticalScrollIndicator={false}
      >
        <View style={styles.heroSection}>
          <Text style={styles.pageTitle}>CAREERS AT ZAPP STUDIOS</Text>
          <View style={styles.fireBar} />
