import React, { useRef, useEffect, useState } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Animated,
  Dimensions,
  Platform
} from 'react-native';
import { useNavigate } from 'react-router-dom';

const HeroSection = ({ onCTAPress }) => {
  const [dimensions, setDimensions] = useState(() => {
    const { width, height } = Dimensions.get('window');
    return { width, height };
  });

  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(30)).current;
  const pulseAnim = useRef(new Animated.Value(1)).current;
  const floatAnim = useRef(new Animated.Value(0)).current;
  const navigate = useNavigate();

  useEffect(() => {
    const updateDimensions = () => {
      const { width, height } = Dimensions.get('window');
      setDimensions({ width, height });
    };

    if (Platform.OS === 'web') {
      window.addEventListener('resize', updateDimensions);
    }

    const subscription = Dimensions.addEventListener('change', updateDimensions);

    return () => {
      if (Platform.OS === 'web') {
        window.removeEventListener('resize', updateDimensions);
      }
      subscription?.remove();
    };
  }, []);

  useEffect(() => {
    // Main entrance animation
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 1000,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 1000,
        useNativeDriver: true,
      }),
    ]).start();

    // Continuous pulse animation for CTA
    Animated.loop(
      Animated.sequence([
        Animated.timing(pulseAnim, {
          toValue: 1.05,
          duration: 2000,
          useNativeDriver: true,
        }),
        Animated.timing(pulseAnim, {
          toValue: 1,
          duration: 2000,
          useNativeDriver: true,
        }),
      ])
    ).start();

    // Floating animation for cards
    Animated.loop(
      Animated.sequence([
        Animated.timing(floatAnim, {
          toValue: -10,
          duration: 3000,
          useNativeDriver: true,
        }),
        Animated.timing(floatAnim, {
          toValue: 0,
          duration: 3000,
          useNativeDriver: true,
        }),
      ])
    ).start();
  }, [fadeAnim, slideAnim, pulseAnim, floatAnim]);

  const handleCTAPress = () => {
    navigate('/book');
  };

  const handleScrollToServices = () => {
    const servicesSection = document.getElementById('services-section');
    if (servicesSection) {
      servicesSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const { width, height } = dimensions;
  const styles = getStyles(width, height);

  return (
    <View style={styles.container}>
      {/* Background layers */}
      <View style={styles.backgroundGradient} />
      <View style={styles.backgroundPattern} />
      <View style={styles.glowOrb1} />
      <View style={styles.glowOrb2} />

      <Animated.View style={[
        styles.content,
        {
          opacity: fadeAnim,
          transform: [{ translateY: slideAnim }]
        }
      ]}>
        {/* Badge */}
        <View style={styles.badge}>
          <View style={styles.badgeDot} />
          <Text style={styles.badgeText}>Now accepting Q1 2025 ventures</Text>
        </View>

        {/* Main headline */}
        <Text style={styles.headline}>
          I Only Build{'\n'}
          <Text style={styles.headlineAccent}>What I Believe In</Text>
        </Text>

        {/* Subheadline */}
        <Text style={styles.subheadline}>
          Technical venture partner who validates ruthlessly, builds with AI-native speed,
          and aligns success through equity partnership.
        </Text>

        {/* Value props in a sleek grid */}
