import React, { useRef } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Animated,
  Dimensions,
  Platform,
} from 'react-native';

const { width } = Dimensions.get('window');

const ProjectCard = ({ project, index }) => {
  const scaleAnim = useRef(new Animated.Value(1)).current;
  const glowAnim = useRef(new Animated.Value(0)).current;

  const handlePressIn = () => {
    Animated.parallel([
      Animated.spring(scaleAnim, {
        toValue: 1.05,
        useNativeDriver: true,
      }),
      Animated.timing(glowAnim, {
        toValue: 1,
        duration: 200,
        useNativeDriver: true,
      }),
    ]).start();
  };

  const handlePressOut = () => {
    Animated.parallel([
      Animated.spring(scaleAnim, {
        toValue: 1,
        useNativeDriver: true,
      }),
      Animated.timing(glowAnim, {
        toValue: 0,
        duration: 200,
        useNativeDriver: true,
      }),
    ]).start();
  };

  return (
    <TouchableOpacity
      activeOpacity={0.9}
      onPressIn={handlePressIn}
      onPressOut={handlePressOut}
    >
      <Animated.View
        style={[
          styles.projectCard,
          {
            transform: [{ scale: scaleAnim }],
          },
        ]}
      >
        <Animated.View
          style={[
            styles.cardGlow,
            {
              opacity: glowAnim,
            },
          ]}
        />

        <View style={styles.cardContent}>
          <View style={styles.cardHeader}>
            <Text style={styles.cardIcon}>{project.icon}</Text>
            <Text style={styles.cardTitle}>{project.title}</Text>
          </View>

          <Text style={styles.cardDescription}>{project.description}</Text>

          <View style={styles.techStack}>
            {project.tech.map((tech, idx) => (
              <View key={idx} style={styles.techBadge}>
                <Text style={styles.techText}>{tech}</Text>
              </View>
            ))}
          </View>

          <View style={styles.impactContainer}>
            <Text style={styles.impactIcon}>üî•</Text>
            <Text style={styles.impactText}>{project.impact}</Text>
          </View>
        </View>
      </Animated.View>
    </TouchableOpacity>
  );
};

const ProjectsSection = () => {
  const projects = [
    {
      id: 1,
      icon: 'ü§ñ',
      title: 'AI Supply Chain Platform',
      description: 'Smart logistics with real-time tracking and predictive analytics. Eliminated delays, automated vendor management.',
      image: 'https://via.placeholder.com/400x300/8B0000/FF4500?text=AI+SUPPLY',
      tech: ['Python', 'TensorFlow', 'React'],
      impact: '300% efficiency increase',
    },
    {
      id: 2,
      icon: '‚õ≥',
      title: 'Golf Group Management App',
      description: 'Complete platform for golf groups. Tee time booking, score tracking, social features. Clean, intuitive UX.',
      image: 'https://via.placeholder.com/400x300/FF4500/000000?text=GOLF+APP',
      tech: ['React Native', 'Firebase', 'Node.js'],
      impact: '250% user engagement boost',
    },
    {
      id: 3,
      icon: 'üêæ',
      title: 'Pet Grooming Platform',
      description: 'End-to-end booking and operations system. Scheduling, payments, customer management. Built for scale.',
      image: 'https://via.placeholder.com/400x300/FFA500/000000?text=PET+CARE',
      tech: ['Next.js', 'PostgreSQL', 'Stripe'],
      impact: '400% revenue growth',
    },
    {
      id: 4,
      icon: 'üöÄ',
      title: 'Startup Development Platform',
      description: 'Full-stack development for emerging businesses. From concept to revenue-generating product.',
      image: 'https://via.placeholder.com/400x300/FFD700/000000?text=STARTUP',
      tech: ['Vue.js', 'AWS', 'MongoDB'],
      impact: '60% faster time to market',
    },
    {
      id: 5,
      icon: 'üí¨',
      title: 'University Advising Chatbot',
      description: 'AI-powered student support using vector embeddings and LLMs. Bridging the gap for overworked staff.',
      image: 'https://via.placeholder.com/400x300/8A2BE2/FFFFFF?text=EDU+BOT',
      tech: ['Python', 'LangChain', 'Next.js'],
      impact: '40% workload reduction',
    },
    {
      id: 6,
      icon: 'ü•ó',
      title: 'DietAI Mobile App',
      description: 'AI-powered calorie tracking app. Fixed critical bugs that led to successful 7-figure PE acquisition.',
      image: 'https://via.placeholder.com/400x300/32CD32/FFFFFF?text=DIETAI',
      tech: ['React Native', 'Firebase', 'Python'],
      impact: '7-figure acquisition',
    },
  ];

  return (
    <View style={styles.container}>
      <Text style={styles.sectionTitle}>RECENT PROJECTS</Text>
      <View style={styles.fireBar} />

      <Text style={styles.sectionSubtitle}>
        Real projects. Real results. Real revenue.
      </Text>

      <View style={styles.projectsGrid}>
        {projects.map((project, index) => (
          <ProjectCard key={project.id} project={project} index={index} />
        ))}
      </View>

      <TouchableOpacity style={styles.viewAllButton}>
        <Text style={styles.viewAllText}>VIEW ALL PROJECTS</Text>
        <Text style={styles.viewAllArrow}>‚Üí</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#0A0A0A',
    paddingVertical: 80,
    paddingHorizontal: width > 768 ? 40 : 20,
    borderTopWidth: 2,
    borderTopColor: '#8B0000',
  },
  sectionTitle: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 36 : 24,
    color: '#FF4500',
    textAlign: 'center',
    marginBottom: 20,
    textShadowColor: '#FF4500',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 20,
  },
  fireBar: {
    height: 4,
    width: 100,
    backgroundColor: '#FF4500',
    alignSelf: 'center',
    marginBottom: 20,
    borderRadius: 2,
    ...Platform.select({
      web: {
        boxShadow: '0 2px 20px rgba(255, 69, 0, 0.8)',
      },
      default: {
        shadowColor: '#FF4500',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.8,
        shadowRadius: 20,
      },
    }),
  },
  sectionSubtitle: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 18 : 14,
    color: '#CCCCCC',
    textAlign: 'center',
    marginBottom: 50,
  },
  projectsGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
    gap: 30,
    maxWidth: 1200,
    alignSelf: 'center',
  },
  projectCard: {
    backgroundColor: '#1A1A1A',
    borderRadius: 12,
    borderWidth: 2,
    borderColor: '#8B0000',
    overflow: 'hidden',
    width: width > 768 ? 480 : '100%',
    maxWidth: 500,
    position: 'relative',
  },
  cardGlow: {
    position: 'absolute',
    top: -2,
    left: -2,
    right: -2,
    bottom: -2,
    borderRadius: 12,
    borderWidth: 2,
    borderColor: '#FF4500',
    ...Platform.select({
      web: {
        boxShadow: '0 0 30px rgba(255, 69, 0, 0.8)',
      },
      default: {
        shadowColor: '#FF4500',
        shadowOffset: { width: 0, height: 0 },
        shadowOpacity: 0.8,
        shadowRadius: 30,
      },
    }),
  },
  cardContent: {
    padding: 25,
  },
  cardHeader: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 15,
  },
  cardIcon: {
    fontSize: 28,
    marginRight: 10,
  },
  cardTitle: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 16 : 14,
    color: '#FFA500',
    flex: 1,
  },
  cardDescription: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: 14,
    color: '#CCCCCC',
    lineHeight: 22,
    marginBottom: 20,
  },
  techStack: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    gap: 8,
    marginBottom: 20,
  },
  techBadge: {
    backgroundColor: 'rgba(255, 69, 0, 0.2)',
    paddingHorizontal: 12,
    paddingVertical: 6,
    borderRadius: 4,
    borderWidth: 1,
    borderColor: '#FF4500',
  },
  techText: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: 11,
    color: '#FFA500',
    fontWeight: '600',
  },
  impactContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: 'rgba(255, 165, 0, 0.1)',
    padding: 12,
    borderRadius: 6,
    borderLeftWidth: 3,
    borderLeftColor: '#FFA500',
  },
  impactIcon: {
    fontSize: 20,
    marginRight: 10,
  },
  impactText: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: 13,
    color: '#FFD700',
    fontWeight: '700',
  },
  viewAllButton: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 50,
    paddingVertical: 15,
    paddingHorizontal: 30,
    backgroundColor: 'transparent',
    borderWidth: 2,
    borderColor: '#FF4500',
    borderRadius: 8,
    alignSelf: 'center',
  },
  viewAllText: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: 14,
    color: '#FF4500',
    marginRight: 10,
  },
  viewAllArrow: {
    fontSize: 20,
    color: '#FF4500',
  },
});

export default ProjectsSection;
