import React, { useRef, useEffect, useState } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Animated,
  Dimensions,
  Platform
} from 'react-native';
import { useNavigate } from 'react-router-dom';
import ServiceGrid from './ServiceGrid';

const HeroSection = ({ onCTAPress }) => {
  const [dimensions, setDimensions] = useState(() => {
    const { width, height } = Dimensions.get('window');
    return { width, height };
  });

  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(20)).current;
  const navigate = useNavigate();

  useEffect(() => {
    const updateDimensions = () => {
      const { width, height } = Dimensions.get('window');
      setDimensions({ width, height });
    };

    if (Platform.OS === 'web') {
      window.addEventListener('resize', updateDimensions);
    }

    const subscription = Dimensions.addEventListener('change', updateDimensions);

    return () => {
      if (Platform.OS === 'web') {
        window.removeEventListener('resize', updateDimensions);
      }
      subscription?.remove();
    };
  }, []);

  useEffect(() => {
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 800,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 800,
        useNativeDriver: true,
      }),
    ]).start();
  }, [fadeAnim, slideAnim]);

  const handleCTAPress = () => {
    navigate('/book');
  };

  const handleEnterpriseClick = () => {
    navigate('/enterprise');
  };

  const services = [
    {
      icon: 'ðŸ’¡',
      title: 'POC Development',
      description: 'Validate your idea with a working prototype in days, not months'
    },
    {
      icon: 'ðŸš€',
      title: 'MVP Building',
      description: 'Launch-ready product in 4-6 weeks with core features that matter'
    },
    {
      icon: 'ðŸ¤–',
      title: 'AI Integration',
      description: 'Leverage cutting-edge AI to build faster and smarter'
    },
    {
      icon: 'ðŸ’°',
      title: 'VC-Ready Products',
      description: 'Built with investor perspective - I know what gets funded'
    }
  ];

  const { width, height } = dimensions;
  const styles = getStyles(width, height);

  return (
    <View style={styles.container}>
      <View style={styles.backgroundGradient} />

      <Animated.View style={[
        styles.content, 
        { 
          opacity: fadeAnim,
          transform: [{ translateY: slideAnim }]
        }
      ]}>
        <Text style={styles.headline}>
          From Idea to Funded MVP in Weeks
        </Text>

        <Text style={styles.subheadline}>
          I build revenue-generating products for ambitious startups
        </Text>

        <ServiceGrid services={services} variant="hero" />

        <View style={styles.trustIndicators}>
          <View style={styles.trustItem}>
            <Text style={styles.trustNumber}>20+</Text>
            <Text style={styles.trustLabel}>MVPs Launched</Text>
          </View>
          <View style={styles.trustDivider} />
          <View style={styles.trustItem}>
            <Text style={styles.trustNumber}>$5M+</Text>
            <Text style={styles.trustLabel}>Raised by Clients</Text>
          </View>
          <View style={styles.trustDivider} />
          <View style={styles.trustItem}>
            <Text style={styles.trustNumber}>Ex-VC</Text>
            <Text style={styles.trustLabel}>Perspective</Text>
          </View>
        </View>

        <TouchableOpacity
          style={styles.ctaButton}
          onPress={handleCTAPress}
          activeOpacity={0.8}
        >
          <Text style={styles.ctaButtonText}>Start Your MVP</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.enterpriseLink}
          onPress={handleEnterpriseClick}
          activeOpacity={0.7}
        >
          <Text style={styles.enterpriseLinkText}>
            Enterprise? AI solutions for established businesses â†’
          </Text>
        </TouchableOpacity>
      </Animated.View>
    </View>
  );
};

const getStyles = (width, height) => StyleSheet.create({
  container: {
    width: width,
    minHeight: height,
    justifyContent: 'center',
    alignItems: 'center',
    position: 'relative',
    paddingTop: 100,
    paddingBottom: 60,
  },
  backgroundGradient: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: '#0F0F14',
    ...Platform.select({
      web: {
        background: 'linear-gradient(180deg, #0F0F14 0%, #1A1A21 100%)',
      },
    }),
  },
  content: {
    alignItems: 'center',
    paddingHorizontal: width > 768 ? 40 : 20,
    zIndex: 1,
    maxWidth: 1200,
    width: '100%',
  },
  headline: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 56 : width > 430 ? 40 : 32,
    fontWeight: '800',
    color: '#F3F4F6',
    textAlign: 'center',
    marginBottom: 24,
    letterSpacing: -1,
    lineHeight: width > 768 ? 64 : width > 430 ? 48 : 40,
  },
  subheadline: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 22 : 18,
    fontWeight: '400',
    color: '#9CA3AF',
    textAlign: 'center',
    marginBottom: 60,
    paddingHorizontal: width > 768 ? 0 : 20,
    lineHeight: width > 768 ? 32 : 28,
    maxWidth: 700,
  },
  trustIndicators: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 60,
    marginBottom: 40,
    paddingVertical: 20,
    paddingHorizontal: 30,
    borderRadius: 12,
    backgroundColor: '#1A1A21',
    borderWidth: 1,
    borderColor: 'rgba(255, 255, 255, 0.06)',
    ...Platform.select({
      web: {
        boxShadow: '0 2px 12px rgba(0, 0, 0, 0.5)',
      },
    }),
  },
  trustItem: {
    alignItems: 'center',
    paddingHorizontal: width > 768 ? 30 : 20,
  },
  trustNumber: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 28 : 24,
    fontWeight: '800',
    color: '#14B8A6',
    marginBottom: 4,
  },
  trustLabel: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 12 : 11,
    color: '#9CA3AF',
    textTransform: 'uppercase',
    letterSpacing: 1,
  },
  trustDivider: {
    width: 1,
    height: 40,
    backgroundColor: 'rgba(255, 255, 255, 0.06)',
  },
  ctaButton: {
    backgroundColor: '#10B981',
    paddingVertical: 18,
    paddingHorizontal: 40,
    borderRadius: 8,
    marginBottom: 24,
    ...Platform.select({
      web: {
        transition: 'all 0.2s ease',
        cursor: 'pointer',
        ':hover': {
          backgroundColor: '#34D399',
          transform: 'translateY(-2px)',
          boxShadow: '0 10px 30px rgba(16, 185, 129, 0.3)',
        },
      },
    }),
  },
  ctaButtonText: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 18 : 16,
    color: '#0F0F14',
    fontWeight: '700',
    letterSpacing: 0.5,
  },
  enterpriseLink: {
    paddingVertical: 10,
    paddingHorizontal: 20,
  },
  enterpriseLinkText: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 15 : 14,
    color: '#6366F1',
    fontWeight: '500',
    textDecorationLine: 'underline',
    textDecorationStyle: 'solid',
    textDecorationColor: 'transparent',
    ...Platform.select({
      web: {
        transition: 'all 0.2s ease',
        ':hover': {
          textDecorationColor: '#6366F1',
        },
      },
    }),
  },
});

export default HeroSection;
