import React, { useRef, useEffect } from 'react';
import {
  View,
  Text,
  Image,
  StyleSheet,
  Animated,
  Dimensions,
  Platform
} from 'react-native';

const { width } = Dimensions.get('window');

const AboutSection = ({ selectedPath }) => {
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(50)).current;

  useEffect(() => {
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 1000,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 1000,
        useNativeDriver: true,
      }),
    ]).start();
  }, [fadeAnim, slideAnim]);

  const bulletPoints = selectedPath === 'enterprise' ? [
    { icon: 'üéØ', text: 'Practical AI training your team can use immediately' },
    { icon: '‚ö°', text: 'Workflow automation without disruption' },
    { icon: 'üìà', text: 'Measurable ROI from day one' },
    { icon: 'üîß', text: 'Custom solutions when off-the-shelf isn\'t enough' },
  ] : [
    { icon: 'üí∞', text: 'Fraction of the cost of traditional agencies' },
    { icon: 'üèÜ', text: 'VC perspective - I know what gets funded' },
    { icon: '‚è±Ô∏è', text: 'Ship in weeks, not months' },
    { icon: 'üíµ', text: 'Products that generate real revenue' },
  ];

  return (
    <View style={styles.container}>
      <Animated.View 
        style={[
          styles.content,
          {
            opacity: fadeAnim,
            transform: [{ translateY: slideAnim }],
          },
        ]}
      >
        <Text style={styles.sectionTitle}>
          {selectedPath === 'enterprise' ? 'WHY WORK WITH ME' : 'WHY I\'M DIFFERENT'}
        </Text>
        <View style={styles.fireBar} />

        <View style={styles.mainContent}>
          <View style={styles.textContent}>
            <Text style={styles.introText}>
              {selectedPath === 'enterprise' 
                ? "The AI revolution is here. Don't get left behind."
                : "Stop talking. Start building. Get funded."}
            </Text>

            <Text style={styles.description}>
              {selectedPath === 'enterprise'
                ? "I've been coding since 2018, mastering the fundamentals before AI tools existed. Now I help companies leverage AI properly - not with buzzwords and complexity, but with practical solutions that work. Most businesses just need to use existing AI tools correctly. I'll teach your team how, and if you need custom solutions, I build those too."
                : "VC background from UT Dallas. Deep technical skills from years before AI. Business acumen to know what matters. I don't just write code - I build products that solve real problems and make real money. At a fraction of what agencies charge."}
            </Text>

            <View style={styles.bulletContainer}>
              {bulletPoints.map((point, index) => (
                <Animated.View 
                  key={index}
                  style={[
                    styles.bulletPoint,
                    {
                      opacity: fadeAnim,
                      transform: [{
                        translateX: slideAnim.interpolate({
                          inputRange: [0, 50],
                          outputRange: [0, -50],
                        }),
                      }],
                    },
                  ]}
                >
                  <Text style={styles.bulletIcon}>{point.icon}</Text>
                  <Text style={styles.bulletText}>{point.text}</Text>
                </Animated.View>
              ))}
            </View>
          </View>

          <View style={styles.imageContainer}>
            <View style={styles.imageFrame}>
              <Image
                source={{ uri: 'https://via.placeholder.com/400x400/FF4500/FFFFFF?text=FOUNDER' }}
                style={styles.founderImage}
                resizeMode="cover"
              />
              <View style={styles.imageGlow} />
            </View>
            <Text style={styles.imageCaption}>AI Innovation Meets Business Strategy</Text>
          </View>
        </View>

        <View style={styles.statsContainer}>
          <View style={styles.statBox}>
            <Text style={styles.statNumber}>7+</Text>
            <Text style={styles.statLabel}>Years of Experience</Text>
          </View>
          <View style={styles.statBox}>
            <Text style={styles.statNumber}>100%</Text>
            <Text style={styles.statLabel}>Client Satisfaction</Text>
          </View>
          <View style={styles.statBox}>
            <Text style={styles.statNumber}>70%</Text>
            <Text style={styles.statLabel}>Off My Rates (Limited Time)</Text>
          </View>
        </View>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#000000',
    paddingVertical: 80,
    paddingHorizontal: width > 768 ? 40 : 20,
    borderTopWidth: 2,
    borderTopColor: '#8B0000',
  },
  content: {
    maxWidth: 1200,
    alignSelf: 'center',
    width: '100%',
  },
  sectionTitle: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 36 : 24,
    color: '#FF4500',
    textAlign: 'center',
    marginBottom: 20,
    textShadowColor: '#FF4500',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 20,
  },
  fireBar: {
    height: 4,
    width: 100,
    backgroundColor: '#FF4500',
    alignSelf: 'center',
    marginBottom: 40,
    borderRadius: 2,
    ...Platform.select({
      web: {
        boxShadow: '0 2px 20px rgba(255, 69, 0, 0.8)',
      },
      default: {
        shadowColor: '#FF4500',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.8,
        shadowRadius: 20,
      },
    }),
  },
  mainContent: {
    flexDirection: width > 768 ? 'row' : 'column',
    alignItems: 'center',
    gap: 40,
  },
  textContent: {
    flex: 1,
  },
  introText: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 20 : 16,
    color: '#FFFFFF',
    marginBottom: 20,
    lineHeight: 28,
  },
  highlight: {
    color: '#FFA500',
    fontWeight: '900',
    textShadowColor: '#FFA500',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 10,
  },
  description: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 16 : 14,
    color: '#CCCCCC',
    marginBottom: 30,
    lineHeight: 24,
  },
  bulletContainer: {
    gap: 15,
  },
  bulletPoint: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: 'rgba(139, 0, 0, 0.2)',
    padding: 15,
    borderRadius: 8,
    borderLeftWidth: 3,
    borderLeftColor: '#FF4500',
  },
  bulletIcon: {
    fontSize: 24,
    marginRight: 15,
  },
  bulletText: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 14 : 12,
    color: '#FFFFFF',
    flex: 1,
  },
  imageContainer: {
    alignItems: 'center',
  },
  imageFrame: {
    position: 'relative',
    borderWidth: 3,
    borderColor: '#FF4500',
    borderRadius: 10,
    overflow: 'hidden',
  },
  founderImage: {
    width: width > 768 ? 300 : 200,
    height: width > 768 ? 300 : 200,
  },
  imageGlow: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'transparent',
    ...Platform.select({
      web: {
        boxShadow: 'inset 0 0 30px rgba(255, 69, 0, 0.3)',
      },
    }),
  },
  imageCaption: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: 12,
    color: '#FFA500',
    marginTop: 15,
    fontStyle: 'italic',
    textAlign: 'center',
  },
  statsContainer: {
    flexDirection: 'row',
    justifyContent: 'center',
    marginTop: 60,
    gap: 30,
    flexWrap: 'wrap',
  },
  statBox: {
    alignItems: 'center',
    padding: width > 768 ? 25 : 20,
    backgroundColor: 'rgba(255, 69, 0, 0.1)',
    borderRadius: 10,
    borderWidth: 1,
    borderColor: '#FF4500',
    minWidth: width > 768 ? 200 : 140,
    flex: width > 768 ? 0 : 1,
    maxWidth: 300,
  },
  statNumber: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 32 : 24,
    color: '#FF4500',
    marginBottom: 10,
    textShadowColor: '#FF4500',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 15,
  },
  statLabel: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 12 : 10,
    color: '#FFFFFF',
    textAlign: 'center',
  },
});

export default AboutSection;
