import React, { useRef, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Animated,
  Dimensions,
  Platform
} from 'react-native';

const { width } = Dimensions.get('window');

const AboutSection = () => {
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(50)).current;

  useEffect(() => {
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 1000,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 1000,
        useNativeDriver: true,
      }),
    ]).start();
  }, [fadeAnim, slideAnim]);

  const bulletPoints = [
    { icon: 'üí∞', text: 'Fraction of the cost of traditional agencies' },
    { icon: 'üèÜ', text: 'VC lens from Austin venture fund training' },
    { icon: '‚è±Ô∏è', text: 'Ship in weeks, not months' },
    { icon: 'üíµ', text: 'Products that generate real revenue' },
  ];

  return (
    <View style={styles.container}>
      <Animated.View 
        style={[
          styles.content,
          {
            opacity: fadeAnim,
            transform: [{ translateY: slideAnim }],
          },
        ]}
      >
        <Text style={styles.sectionTitle}>WHY I'M DIFFERENT</Text>

        <View style={styles.mainContent}>
          <View style={styles.textContent}>
            <Text style={styles.introText}>
              Stop talking. Start building. Get funded.
            </Text>

            <Text style={styles.description}>
              VC training from UT Dallas class taught by Austin venture fund president. Deep technical skills from years before AI. Business acumen to know what matters. I don't just write code - I build products that solve real problems and make real money. At a fraction of what agencies charge.
            </Text>

            <View style={styles.bulletContainer}>
              {bulletPoints.map((point, index) => (
                <Animated.View 
                  key={index}
                  style={[
                    styles.bulletPoint,
                    {
                      opacity: fadeAnim,
                      transform: [{
                        translateX: slideAnim.interpolate({
                          inputRange: [0, 50],
                          outputRange: [0, -50],
                        }),
                      }],
                    },
                  ]}
                >
                  <Text style={styles.bulletIcon}>{point.icon}</Text>
                  <Text style={styles.bulletText}>{point.text}</Text>
                </Animated.View>
              ))}
            </View>
          </View>

          <View style={styles.imageContainer}>
            <View style={styles.imageFrame}>
              <View style={styles.imagePlaceholder}>
                <Text style={styles.placeholderText}>FOUNDER</Text>
              </View>
            </View>
            <Text style={styles.imageCaption}>AI Innovation Meets Business Strategy</Text>
          </View>
        </View>

        <View style={styles.statsContainer}>
          <View style={styles.statBox}>
            <Text style={styles.statNumber}>7+</Text>
            <Text style={styles.statLabel}>Years of Experience</Text>
          </View>
          <View style={styles.statBox}>
            <Text style={styles.statNumber}>99%</Text>
            <Text style={styles.statLabel}>Client Satisfaction</Text>
          </View>
          <View style={styles.statBox}>
            <Text style={styles.statNumber}>70%</Text>
            <Text style={styles.statLabel}>Off My Rates (Limited Time)</Text>
          </View>
        </View>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#0A0A0F',
    paddingVertical: 80,
    paddingHorizontal: width > 768 ? 40 : 20,
    borderTopWidth: 1,
    borderTopColor: 'rgba(255, 255, 255, 0.08)',
  },
  content: {
    maxWidth: 1200,
    alignSelf: 'center',
    width: '100%',
  },
  sectionTitle: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 36 : 28,
    color: '#FFFFFF',
    fontWeight: '700',
    textAlign: 'center',
    marginBottom: 50,
    letterSpacing: -0.5,
  },
  mainContent: {
    flexDirection: width > 768 ? 'row' : 'column',
    alignItems: 'center',
    gap: 40,
  },
  textContent: {
    flex: 1,
  },
  introText: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 20 : 16,
    color: '#FFFFFF',
    marginBottom: 20,
    lineHeight: 28,
  },
  highlight: {
    color: '#FFA500',
    fontWeight: '900',
    textShadowColor: '#FFA500',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 10,
  },
  description: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 16 : 14,
    color: '#CCCCCC',
    marginBottom: 30,
    lineHeight: 24,
  },
  bulletContainer: {
    gap: 15,
  },
  bulletPoint: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: 'rgba(139, 0, 0, 0.2)',
    padding: 15,
    borderRadius: 8,
    borderLeftWidth: 3,
    borderLeftColor: '#FF4500',
  },
  bulletIcon: {
    fontSize: 24,
    marginRight: 15,
  },
  bulletText: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 14 : 12,
    color: '#FFFFFF',
    flex: 1,
  },
  imageContainer: {
    alignItems: 'center',
  },
  imageFrame: {
    position: 'relative',
    borderWidth: 2,
    borderColor: '#14B8A6',
    borderRadius: 16,
    overflow: 'hidden',
    ...Platform.select({
      web: {
        boxShadow: '0 0 30px rgba(20, 184, 166, 0.3)',
      },
    }),
  },
  imagePlaceholder: {
    width: width > 768 ? 300 : 200,
    height: width > 768 ? 300 : 200,
    backgroundColor: '#1A1A21',
    justifyContent: 'center',
    alignItems: 'center',
  },
  placeholderText: {
    fontFamily: Platform.OS === 'web' ? "'Inter', sans-serif" : 'sans-serif',
    fontSize: 18,
    color: '#6366F1',
    fontWeight: '700',
    letterSpacing: 2,
  },
  founderImage: {
    width: width > 768 ? 300 : 200,
    height: width > 768 ? 300 : 200,
  },
  imageGlow: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'transparent',
    ...Platform.select({
      web: {
        boxShadow: 'inset 0 0 30px rgba(255, 69, 0, 0.3)',
      },
    }),
  },
  imageCaption: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: 12,
    color: '#FFA500',
    marginTop: 15,
    fontStyle: 'italic',
    textAlign: 'center',
  },
  statsContainer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginTop: 60,
    gap: 20,
    flexWrap: 'wrap',
  },
  statBox: {
    alignItems: 'center',
    padding: width > 768 ? 20 : 15,
    backgroundColor: 'rgba(255, 69, 0, 0.1)',
    borderRadius: 10,
    borderWidth: 1,
    borderColor: '#FF4500',
    minWidth: width > 768 ? 150 : 100,
    flex: width > 768 ? 0 : 1,
  },
  statNumber: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 32 : 24,
    color: '#FF4500',
    marginBottom: 10,
    textShadowColor: '#FF4500',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 15,
  },
  statLabel: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 12 : 10,
    color: '#FFFFFF',
    textAlign: 'center',
  },
});

export default AboutSection;
