import React, { useState, useRef } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Animated,
  Dimensions,
  Platform,
  Alert
} from 'react-native';

const { width } = Dimensions.get('window');

const ContactSection = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const successAnim = useRef(new Animated.Value(0)).current;
  const buttonScale = useRef(new Animated.Value(1)).current;

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!emailRegex.test(formData.email)) {
      newErrors.email = 'Invalid email format';
    }

    if (!formData.message.trim()) {
      newErrors.message = 'Message is required';
    } else if (formData.message.length < 10) {
      newErrors.message = 'Message must be at least 10 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (!validateForm()) {
      Animated.sequence([
        Animated.timing(buttonScale, {
          toValue: 0.95,
          duration: 100,
          useNativeDriver: true,
        }),
        Animated.timing(buttonScale, {
          toValue: 1,
          duration: 100,
          useNativeDriver: true,
        }),
      ]).start();
      return;
    }

    setIsSubmitting(true);

    setTimeout(() => {
      setIsSubmitting(false);
      setIsSubmitted(true);

      Animated.sequence([
        Animated.timing(successAnim, {
          toValue: 1,
          duration: 500,
          useNativeDriver: true,
        }),
        Animated.delay(2000),
        Animated.timing(successAnim, {
          toValue: 0,
          duration: 500,
          useNativeDriver: true,
        }),
      ]).start(() => {
        setFormData({ name: '', email: '', message: '' });
        setIsSubmitted(false);
      });

      if (Platform.OS === 'web') {
        console.log('Form submitted:', formData);
      } else {
        Alert.alert(
          'Message Sent!',
          'Your message has been ignited and sent to Zapp Studios!',
          [{ text: 'OK' }]
        );
      }
    }, 1500);
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.sectionTitle}>IGNITE A CONVERSATION</Text>
      <View style={styles.fireBar} />

      <Text style={styles.sectionSubtitle}>
        Ready to transform your business? Let's talk.
      </Text>

      <View style={styles.formContainer}>
        <View style={styles.inputGroup}>
          <Text style={styles.inputLabel}>NAME</Text>
          <TextInput
            style={[styles.input, errors.name && styles.inputError]}
            placeholder="Enter your name"
            placeholderTextColor="#666"
            value={formData.name}
            onChangeText={(text) => handleInputChange('name', text)}
            autoCapitalize="words"
          />
          {errors.name && <Text style={styles.errorText}>{errors.name}</Text>}
        </View>

        <View style={styles.inputGroup}>
          <Text style={styles.inputLabel}>EMAIL</Text>
          <TextInput
            style={[styles.input, errors.email && styles.inputError]}
            placeholder="Enter your email"
            placeholderTextColor="#666"
            value={formData.email}
            onChangeText={(text) => handleInputChange('email', text)}
            keyboardType="email-address"
            autoCapitalize="none"
          />
          {errors.email && <Text style={styles.errorText}>{errors.email}</Text>}
        </View>

        <View style={styles.inputGroup}>
          <Text style={styles.inputLabel}>MESSAGE</Text>
          <TextInput
            style={[styles.textArea, errors.message && styles.inputError]}
            placeholder="Tell us about your project..."
            placeholderTextColor="#666"
            value={formData.message}
            onChangeText={(text) => handleInputChange('message', text)}
            multiline
            numberOfLines={6}
            textAlignVertical="top"
          />
          {errors.message && <Text style={styles.errorText}>{errors.message}</Text>}
        </View>

        <TouchableOpacity
          style={[styles.submitButton, isSubmitting && styles.submitButtonDisabled]}
          onPress={handleSubmit}
          disabled={isSubmitting || isSubmitted}
          activeOpacity={0.8}
        >
          <Animated.View
            style={[
              styles.submitButtonInner,
              { transform: [{ scale: buttonScale }] }
            ]}
          >
            <Text style={styles.submitButtonText}>
              {isSubmitting ? 'IGNITING...' : isSubmitted ? 'MESSAGE SENT!' : 'IGNITE A CONVERSATION'}
            </Text>
            {!isSubmitting && !isSubmitted && (
              <Text style={styles.submitArrow}>üî•</Text>
            )}
          </Animated.View>
        </TouchableOpacity>

        <Animated.View
          style={[
            styles.successMessage,
            {
              opacity: successAnim,
              transform: [{
                translateY: successAnim.interpolate({
                  inputRange: [0, 1],
                  outputRange: [20, 0],
                }),
              }],
            },
          ]}
        >
          <Text style={styles.successText}>üî• Your message has been ignited! üî•</Text>
        </Animated.View>
      </View>

      <View style={styles.contactInfo}>
        <View style={styles.contactItem}>
          <Text style={styles.contactIcon}>üìß</Text>
          <Text style={styles.contactText}>hello@zappstudios.com</Text>
        </View>
        <View style={styles.contactItem}>
          <Text style={styles.contactIcon}>üí¨</Text>
          <Text style={styles.contactText}>Discord: ZappStudios</Text>
        </View>
        <View style={styles.contactItem}>
          <Text style={styles.contactIcon}>üê¶</Text>
          <Text style={styles.contactText}>@ZappStudios</Text>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#000000',
    paddingVertical: 80,
    paddingHorizontal: width > 768 ? 40 : 20,
    borderTopWidth: 2,
    borderTopColor: '#8B0000',
  },
  sectionTitle: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 36 : 24,
    color: '#FF4500',
    textAlign: 'center',
    marginBottom: 20,
    textShadowColor: '#FF4500',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 20,
  },
  fireBar: {
    height: 4,
    width: 100,
    backgroundColor: '#FF4500',
    alignSelf: 'center',
    marginBottom: 20,
    borderRadius: 2,
    ...Platform.select({
      web: {
        boxShadow: '0 2px 20px rgba(255, 69, 0, 0.8)',
      },
      default: {
        shadowColor: '#FF4500',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.8,
        shadowRadius: 20,
      },
    }),
  },
  sectionSubtitle: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 18 : 14,
    color: '#CCCCCC',
    textAlign: 'center',
    marginBottom: 50,
  },
  formContainer: {
    maxWidth: 600,
    alignSelf: 'center',
    width: '100%',
    backgroundColor: 'rgba(26, 26, 26, 0.5)',
    padding: 30,
    borderRadius: 12,
    borderWidth: 2,
    borderColor: '#8B0000',
  },
  inputGroup: {
    marginBottom: 25,
  },
  inputLabel: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: 12,
    color: '#FF4500',
    fontWeight: '700',
    letterSpacing: 2,
    marginBottom: 8,
