import React from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Platform,
  Dimensions,
  Linking
} from 'react-native';
import { useNavigate } from 'react-router-dom';

const { width } = Dimensions.get('window');

const NavigationBar = ({ activeSection, onNavigate }) => {
  const navigate = useNavigate();

  const handleBookNow = () => {
    navigate('/book');
  };

  const handleLogoClick = () => {
    if (Platform.OS === 'web') {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    } else {
      onNavigate('home');
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.navbar}>
        <TouchableOpacity 
          style={styles.logoContainer}
          onPress={handleLogoClick}
          activeOpacity={0.7}
        >
          <Text style={styles.logo}>ZAPP</Text>
          <Text style={styles.logoAccent}>STUDIOS</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.bookButton}
          onPress={handleBookNow}
          activeOpacity={0.8}
        >
          <Text style={styles.bookButtonText}>BOOK NOW</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    position: Platform.OS === 'web' ? 'fixed' : 'absolute',
    top: 0,
    left: 0,
    right: 0,
    zIndex: 1000,
    backgroundColor: 'rgba(0, 0, 0, 0.95)',
    ...Platform.select({
      web: {
        backdropFilter: 'blur(10px)',
      },
    }),
  },
  navbar: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    paddingHorizontal: width > 768 ? 40 : 20,
    paddingVertical: 20,
    borderBottomWidth: 2,
    borderBottomColor: '#FF4500',
    ...Platform.select({
      web: {
        boxShadow: '0 4px 20px rgba(255, 69, 0, 0.3)',
      },
      default: {
        shadowColor: '#FF4500',
        shadowOffset: { width: 0, height: 4 },
        shadowOpacity: 0.3,
        shadowRadius: 20,
        elevation: 10,
      },
    }),
  },
  logoContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    cursor: Platform.OS === 'web' ? 'pointer' : 'default',
  },
  logo: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 24 : 18,
    color: '#FF4500',
    fontWeight: '900',
  },
  logoAccent: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 24 : 18,
    color: '#FFFFFF',
    fontWeight: '900',
    marginLeft: 8,
  },
  bookButton: {
    backgroundColor: '#FF4500',
    paddingVertical: 12,
    paddingHorizontal: width > 768 ? 30 : 20,
    borderRadius: 6,
    ...Platform.select({
      web: {
        transition: 'all 0.3s ease',
        ':hover': {
          backgroundColor: '#FF6500',
          transform: 'translateY(-1px)',
        },
      },
    }),
  },
  bookButtonText: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 14 : 12,
    color: '#000000',
    fontWeight: '700',
    letterSpacing: 1,
  },
});

export default NavigationBar;
