import React from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Dimensions,
  Platform,
  Linking,
} from 'react-native';

const { width } = Dimensions.get('window');

const ContactSection = () => {
  const handleBookCall = () => {
    Linking.openURL('https://calendly.com/zappstudios');
  };

  return (
    <View style={styles.container}>
      <Text style={styles.sectionTitle}>LET'S TALK</Text>
      <View style={styles.fireBar} />

      <Text style={styles.sectionSubtitle}>
        Ready to bring your idea to life? Schedule a free strategy session to discuss your vision.
      </Text>

      <TouchableOpacity
        style={styles.ctaButton}
        onPress={handleBookCall}
        activeOpacity={0.8}
      >
        <Text style={styles.ctaButtonText}>BOOK A FREE STRATEGY CALL</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#F5F5F5',
    paddingVertical: 80,
    paddingHorizontal: width > 768 ? 40 : 20,
    alignItems: 'center',
    borderTopWidth: 1,
    borderTopColor: '#EEEEEE',
  },
  sectionTitle: {
    fontFamily: Platform.OS === 'web' ? "'Montserrat', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 36 : 24,
    color: '#1A1A1A',
    textAlign: 'center',
    marginBottom: 20,
    fontWeight: '700',
  },
  fireBar: {
    height: 4,
    width: 80,
    backgroundColor: '#00A8E8',
    alignSelf: 'center',
    marginBottom: 20,
    borderRadius: 2,
  },
  sectionSubtitle: {
    fontFamily: Platform.OS === 'web' ? "'Roboto', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 18 : 14,
    color: '#333333',
    textAlign: 'center',
    marginBottom: 50,
    maxWidth: 600,
    lineHeight: 28,
  },
  ctaButton: {
    backgroundColor: '#00A8E8',
    paddingVertical: 20,
    paddingHorizontal: 40,
    borderRadius: 8,
    alignItems: 'center',
  },
  ctaButtonText: {
    fontFamily: Platform.OS === 'web' ? "'Montserrat', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 16 : 12,
    color: '#FFFFFF',
    fontWeight: '700',
    letterSpacing: 1,
  },
});

export default ContactSection;
