import React, { useState, useRef } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Animated,
  Dimensions,
  Platform,
  Linking
} from 'react-native';

const { width } = Dimensions.get('window');

const ContactSection = () => {
  const handlePress = () => {
    Linking.openURL('https://calendly.com/zappstudios');
  };

  return (
    <View style={styles.container}>
      <Text style={styles.sectionTitle}>BOOK A STRATEGY CALL</Text>
      <View style={styles.fireBar} />

      <Text style={styles.sectionSubtitle}>
        Ready to turn your idea into a revenue-generating reality? Let's talk.
      </Text>

      <TouchableOpacity
        style={styles.ctaButton}
        onPress={handlePress}
        activeOpacity={0.8}
      >
        <View style={styles.ctaButtonInner}>
          <Text style={styles.ctaButtonText}>SCHEDULE YOUR CALL</Text>
          <View style={styles.ctaButtonGlow} />
        </View>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#000000',
    paddingVertical: 80,
    paddingHorizontal: width > 768 ? 40 : 20,
    borderTopWidth: 2,
    borderTopColor: '#8B0000',
  },
  sectionTitle: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 36 : 24,
    color: '#FF4500',
    textAlign: 'center',
    marginBottom: 20,
    textShadowColor: '#FF4500',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 20,
  },
  fireBar: {
    height: 4,
    width: 100,
    backgroundColor: '#FF4500',
    alignSelf: 'center',
    marginBottom: 20,
    borderRadius: 2,
    ...Platform.select({
      web: {
        boxShadow: '0 2px 20px rgba(255, 69, 0, 0.8)',
      },
      default: {
        shadowColor: '#FF4500',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.8,
        shadowRadius: 20,
      },
    }),
  },
  sectionSubtitle: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 18 : 14,
    color: '#CCCCCC',
    textAlign: 'center',
    marginBottom: 50,
  },
  ctaButton: {
    alignSelf: 'center',
  },
  ctaButtonInner: {
    backgroundColor: '#FF4500',
    paddingVertical: 20,
    paddingHorizontal: 40,
    borderRadius: 8,
    borderWidth: 2,
    borderColor: '#FFA500',
    position: 'relative',
    overflow: 'hidden',
  },
  ctaButtonText: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 16 : 12,
    color: '#000000',
    fontWeight: '900',
    letterSpacing: 1,
  },
  ctaButtonGlow: {
    position: 'absolute',
    top: -50,
    left: -50,
    right: -50,
    bottom: -50,
    backgroundColor: 'transparent',
    ...Platform.select({
      web: {
        boxShadow: '0 0 40px rgba(255, 69, 0, 0.8)',
      },
      default: {
        shadowColor: '#FF4500',
        shadowOffset: { width: 0, height: 0 },
        shadowOpacity: 0.8,
        shadowRadius: 40,
      },
    }),
  },
});

export default ContactSection;
