import React from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Platform,
  Dimensions
} from 'react-native';

const { width } = Dimensions.get('window');

const NavigationBar = ({ activeSection, onNavigate }) => {

  const navItems = [
    { id: 'home', label: 'HOME' },
    { id: 'about', label: 'ABOUT' },
    { id: 'projects', label: 'PROJECTS' },
    { id: 'how', label: 'PROCESS' },
    { id: 'contact', label: 'CONTACT' },
    { id: 'careers', label: 'PARTNERSHIPS', isExternal: true },
  ];

  return (
    <View style={styles.container}>
      <View style={styles.navbar}>
        <View style={styles.logoContainer}>
          <Text style={styles.logo}>ZAPP</Text>
          <Text style={styles.logoAccent}>STUDIOS</Text>
        </View>

        <View style={styles.navItems}>
          {navItems.map((item) => (
            <TouchableOpacity
              key={item.id}
              style={[
                styles.navItem,
                activeSection === item.id && styles.navItemActive
              ]}
              onPress={() => onNavigate(item.id)}
              activeOpacity={0.7}
            >
              <Text style={[
                styles.navText,
                activeSection === item.id && styles.navTextActive
              ]}>
                {item.label}
              </Text>
              {activeSection === item.id && (
                <View style={styles.activeIndicator} />
              )}
            </TouchableOpacity>
          ))}
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    position: Platform.OS === 'web' ? 'fixed' : 'absolute',
    top: 0,
    left: 0,
    right: 0,
    zIndex: 1000,
    backgroundColor: 'rgba(245, 245, 245, 0.8)',
    ...Platform.select({
      web: {
        backdropFilter: 'blur(10px)',
      },
    }),
  },
  navbar: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    paddingHorizontal: width > 768 ? 40 : 20,
    paddingVertical: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#DDDDDD',
  },
  logoContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  logo: {
    fontFamily: Platform.OS === 'web' ? "'Montserrat', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 24 : 18,
    color: '#1A1A1A',
    fontWeight: '700',
  },
  logoAccent: {
    fontFamily: Platform.OS === 'web' ? "'Montserrat', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 24 : 18,
    color: '#00A8E8',
    fontWeight: '700',
    marginLeft: 8,
  },
  navItems: {
    flexDirection: 'row',
    alignItems: 'center',
    gap: width > 768 ? 30 : 15,
  },
  navItem: {
    paddingVertical: 8,
    paddingHorizontal: width > 768 ? 16 : 8,
    position: 'relative',
  },
  navItemActive: {},
  navText: {
    fontFamily: Platform.OS === 'web' ? "'Montserrat', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 14 : 12,
    fontWeight: '600',
    color: '#1A1A1A',
    opacity: 0.7,
    letterSpacing: 1,
  },
  navTextActive: {
    color: '#00A8E8',
    opacity: 1,
  },
  activeIndicator: {
    position: 'absolute',
    bottom: 0,
    left: '20%',
    right: '20%',
    height: 2,
    backgroundColor: '#00A8E8',
    borderRadius: 1,
  },
});

export default NavigationBar;
