import React, { useRef, useEffect } from 'react';
import {
  View,
  Text,
  Image,
  StyleSheet,
  Animated,
  Dimensions,
  Platform
} from 'react-native';

const { width } = Dimensions.get('window');

const AboutSection = () => {
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(50)).current;

  useEffect(() => {
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 1000,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 1000,
        useNativeDriver: true,
      }),
    ]).start();
  }, [fadeAnim, slideAnim]);

  const bulletPoints = [
    { text: 'Transforming startup ideas into market-ready products' },
    { text: 'Building scalable solutions that drive user growth and revenue' },
    { text: 'A true technical partner invested in your success' },
    { text: 'Products that secure funding and attract customers' },
  ];

  return (
    <View style={styles.container}>
      <Animated.View 
        style={[
          styles.content,
          {
            opacity: fadeAnim,
            transform: [{ translateY: slideAnim }],
          },
        ]}
      >
        <Text style={styles.sectionTitle}>ABOUT ME</Text>
        <View style={styles.fireBar} />

        <View style={styles.mainContent}>
          <View style={styles.textContent}>
            <Text style={styles.introText}>
              I'm not just a developer; I'm a product-minded engineer with a deep understanding of business.
            </Text>

            <Text style={styles.description}>
              Since 2018, I've been honing my coding skills, mastering the fundamentals long before the AI boom. This foundation allows me to architect robust, custom solutions to your most pressing business problems. My education in entrepreneurship at UT-Dallas, including a Venture Capital class with Bryan Chambers of Capital Factory, has given me a unique lens through which I view product development. I don't just build features; I build revenue-generating products that deliver tangible value to your customers.
            </Text>

            <View style={styles.bulletContainer}>
              {bulletPoints.map((point, index) => (
                <Animated.View 
                  key={index}
                  style={[
                    styles.bulletPoint,
                    {
                      opacity: fadeAnim,
                      transform: [{
                        translateX: slideAnim.interpolate({
                          inputRange: [0, 50],
                          outputRange: [0, -50],
                        }),
                      }],
                    },
                  ]}
                >
                  <Text style={styles.bulletIcon}>â€¢</Text>
                  <Text style={styles.bulletText}>{point.text}</Text>
                </Animated.View>
              ))}
            </View>
          </View>

          <View style={styles.imageContainer}>
            <View style={styles.imageFrame}>
              <Image
                source={{ uri: 'https://via.placeholder.com/400x400/FF4500/FFFFFF?text=FOUNDER' }}
                style={styles.founderImage}
                resizeMode="cover"
              />
              <View style={styles.imageGlow} />
            </View>
            <Text style={styles.imageCaption}>Building Tomorrow's Solutions Today</Text>
          </View>
        </View>

        <View style={styles.statsContainer}>
          <View style={styles.statBox}>
            <Text style={styles.statNumber}>6+</Text>
            <Text style={styles.statLabel}>Years of Experience</Text>
          </View>
          <View style={styles.statBox}>
            <Text style={styles.statNumber}>10+</Text>
            <Text style={styles.statLabel}>MVPs Launched</Text>
          </View>
          <View style={styles.statBox}>
            <Text style={styles.statNumber}>100%</Text>
            <Text style={styles.statLabel}>Client Satisfaction</Text>
          </View>
        </View>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#FFFFFF',
    paddingVertical: 80,
    paddingHorizontal: width > 768 ? 40 : 20,
    borderTopWidth: 1,
    borderTopColor: '#EEEEEE',
  },
  content: {
    maxWidth: 1200,
    alignSelf: 'center',
    width: '100%',
  },
  sectionTitle: {
    fontFamily: Platform.OS === 'web' ? "'Montserrat', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 36 : 24,
    color: '#1A1A1A',
    textAlign: 'center',
    marginBottom: 20,
    fontWeight: '700',
  },
  fireBar: {
    height: 4,
    width: 80,
    backgroundColor: '#00A8E8',
    alignSelf: 'center',
    marginBottom: 40,
    borderRadius: 2,
  },
  mainContent: {
    flexDirection: width > 768 ? 'row' : 'column',
    alignItems: 'center',
    gap: 40,
  },
  textContent: {
    flex: 1,
  },
  introText: {
    fontFamily: Platform.OS === 'web' ? "'Montserrat', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 20 : 16,
    color: '#1A1A1A',
    marginBottom: 20,
    lineHeight: 32,
    fontWeight: '600',
  },
  highlight: {
    color: '#00A8E8',
    fontWeight: '700',
  },
  description: {
    fontFamily: Platform.OS === 'web' ? "'Roboto', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 16 : 14,
    color: '#333333',
    marginBottom: 30,
    lineHeight: 28,
  },
  bulletContainer: {
    gap: 15,
  },
  bulletPoint: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  bulletIcon: {
    fontSize: 24,
    marginRight: 15,
    color: '#00A8E8',
  },
  bulletText: {
    fontFamily: Platform.OS === 'web' ? "'Roboto', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 14 : 12,
    color: '#333333',
    flex: 1,
  },
  imageContainer: {
    alignItems: 'center',
  },
  imageFrame: {
    position: 'relative',
    borderWidth: 3,
    borderColor: '#00A8E8',
    borderRadius: 10,
    overflow: 'hidden',
  },
  founderImage: {
    width: width > 768 ? 300 : 200,
    height: width > 768 ? 300 : 200,
  },
  imageGlow: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'transparent',
  },
  imageCaption: {
    fontFamily: Platform.OS === 'web' ? "'Montserrat', sans-serif" : 'sans-serif',
    fontSize: 12,
    color: '#00A8E8',
    marginTop: 15,
    fontStyle: 'italic',
    textAlign: 'center',
  },
  statsContainer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginTop: 60,
    gap: 20,
    flexWrap: 'wrap',
  },
  statBox: {
    alignItems: 'center',
    padding: 20,
    backgroundColor: '#FFFFFF',
    borderRadius: 10,
    borderWidth: 1,
    borderColor: '#EEEEEE',
    minWidth: width > 768 ? 150 : 100,
  },
  statNumber: {
    fontFamily: Platform.OS === 'web' ? "'Montserrat', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 32 : 24,
    color: '#00A8E8',
    marginBottom: 10,
    fontWeight: '700',
  },
  statLabel: {
    fontFamily: Platform.OS === 'web' ? "'Roboto', sans-serif" : 'sans-serif',
    fontSize: width > 768 ? 12 : 10,
    color: '#333333',
    textAlign: 'center',
  },
});

export default AboutSection;
