import React from 'react';
import {
  View,
  Text,
  StyleSheet,
  Dimensions,
  Platform
} from 'react-native';

const { width } = Dimensions.get('window');

const Footer = () => {
  const currentYear = new Date().getFullYear();

  return (
    <View style={styles.container}>
      <View style={styles.content}>
        <Text style={styles.logo}>ZAPP STUDIOS</Text>
        <Text style={styles.tagline}>Powered by Fiery Innovation</Text>

        <View style={styles.divider} />

        <Text style={styles.copyright}>
          Â© {currentYear} Zapp Studios | All Rights Reserved
        </Text>

        <View style={styles.fireIcons}>
          <Text style={styles.fireIcon}>ðŸ”¥</Text>
          <Text style={styles.builtWith}>Built with passion & code</Text>
          <Text style={styles.fireIcon}>ðŸ”¥</Text>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#0A0A0A',
    paddingVertical: 40,
    paddingHorizontal: width > 768 ? 40 : 20,
    borderTopWidth: 2,
    borderTopColor: '#8B0000',
  },
  content: {
    alignItems: 'center',
  },
  logo: {
    fontFamily: Platform.OS === 'web' ? "'Press Start 2P', monospace" : 'monospace',
    fontSize: width > 768 ? 24 : 18,
    color: '#FF4500',
    marginBottom: 10,
    textShadowColor: '#FF4500',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 15,
  },
  tagline: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: 14,
    color: '#FFA500',
    marginBottom: 20,
    fontStyle: 'italic',
  },
  divider: {
    width: 100,
    height: 2,
    backgroundColor: '#FF4500',
    marginBottom: 20,
    opacity: 0.5,
  },
  copyright: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: 12,
    color: '#666',
    marginBottom: 15,
  },
  fireIcons: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  fireIcon: {
    fontSize: 16,
    marginHorizontal: 10,
  },
  builtWith: {
    fontFamily: Platform.OS === 'web' ? "'Orbitron', sans-serif" : 'sans-serif',
    fontSize: 11,
    color: '#999',
  },
});

export default Footer;
